CREATE TABLE "alert" (
  "id" INTEGER PRIMARY KEY NOT NULL, 
  "title" TEXT NOT NULL, 
  "date" TIMESTAMP NOT NULL,
  "enabled" BOOLEAN DEFAULT 0
);

CREATE TABLE "color" (
  "id" TEXT NOT NULL,
  "foreground" TEXT NOT NULL,
  "background" TEXT NOT NULL,
  PRIMARY KEY ("id")
);

CREATE TABLE "config" (
  "id" TEXT NOT NULL, 
  "value" TEXT, 
  PRIMARY KEY ("id")
);

CREATE TABLE "credential" (
  "id" INTEGER PRIMARY KEY NOT NULL, 
  "name" TEXT NOT NULL, 
  "loginid" TEXT NOT NULL, 
  "password" TEXT NOT NULL 
);

CREATE TABLE "language" (
  "id" TEXT NOT NULL, 
  "name" TEXT NOT NULL, 
  PRIMARY KEY ("id")
);

CREATE TABLE "link" (
  "id" INTEGER PRIMARY KEY NOT NULL,
  "name" TEXT NOT NULL, 
  "url" TEXT NOT NULL, 
  "notes" TEXT, 
  "credentialid" INTEGER NOT NULL DEFAULT 0, 
  "added" TIMESTAMP NOT NULL
);
CREATE INDEX "link_credentialid" ON "link" ("credentialid");

CREATE TABLE "published" (
  "id" INTEGER PRIMARY KEY NOT NULL,
  "titleid" INTEGER NOT NULL,
  "publisherid" INTEGER NOT NULL DEFAULT 0,
  "added" TIMESTAMP NOT NULL,
  "published" TIMESTAMP,
  "url" TEXT,
  "notes" TEXT
);
CREATE INDEX "published_publisherid" ON "published" ("publisherid");
CREATE INDEX "published_titleid" ON "published" ("titleid");

CREATE TABLE "response" (
  "id" INTEGER PRIMARY KEY NOT NULL, 
  "name" TEXT, 
  "description" TEXT 
);

CREATE TABLE "schema" (
  "id" INTEGER PRIMARY KEY NOT NULL, 
  "version" INTEGER NOT NULL DEFAULT 0, 
  "ts" TIMESTAMP NOT NULL DEFAULT 0 
);

CREATE TABLE "submissionmessageattachment" (
  "id" INTEGER PRIMARY KEY NOT NULL, 
  "messageid" INTEGER NOT NULL, 
  "display" TEXT NOT NULL, 
  "filename" TEXT NOT NULL, 
  "filesize" INTEGER NOT NULL, 
  "type" INTEGER NOT NULL DEFAULT 0 
);
CREATE INDEX "submissionmessageattachment_messageid" ON "submissionmessageattachment" ("messageid");

CREATE TABLE "tag" (
  "id" INTEGER PRIMARY KEY NOT NULL, 
  "tag" TEXT NOT NULL, 
  "description" TEXT NOT NULL, 
  "usagecount" INTEGER NOT NULL DEFAULT 0
);

CREATE TABLE "titletag" (
  "titleid" INTEGER NOT NULL, 
  "tagid" INTEGER NOT NULL, 
  PRIMARY KEY ("titleid", "tagid")
);

CREATE TABLE "usernote" (
  "id" INTEGER PRIMARY KEY NOT NULL, 
  "title" TEXT NOT NULL, 
  "created" TIMESTAMP NOT NULL, 
  "note" TEXT 
);
