INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (38, 'Roanoke Review', 'English Department', 'Roanoke College', '', 'Salem', 'VA', '24153', 'www.roanoke.edu/roanokereview/', 'review@roanoke.edu', 8, 0, NULL, NULL, 'Paul S. Hanstedt, Editor\nEmail : review@roanoke.edu\n\nThe Roanoke Review publishes poetry and short fiction annually. Submissions are accepted from September 1-February 1. We select poems up to 100 lines in length and fiction from 2,000-7,500 words. You may send up to five poems per submission, though we do not accept multiple submissions of fiction. Simultaneous submissions of either genre are fine.\n\nManuscripts will be returned if accompanied by a self-addressed, stamped envelope. A cover letter is preferred.\n\nSample copies are available for $5.00 including postage. Two-year subscriptions are available for $13.00.\n\nPlease allow 10-12 weeks for response.\nCOPYRIGHT INFORMATION\nThe Roanoke Review owns first rights of any materials it publishes.', '2006-06-29 00:00:00');
