INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (159, 'Writers On The Edge, Inc', 'P.O. Box 85', '', '', 'Newport', 'OR', '97365', 'writersontheedge.org', 'info@writersontheedge.org', 8, 1, '2010-12-26 00:00:00', NULL, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\b\\f0\\fs20 Presenter Information\\b0\\par\r\n\\par\r\nTo be considered as a featured writer at the Nye Beach Writers'' Series you must present original work. Events are generally held the third Saturday of every month through November.\\par\r\n\\par\r\n    Please send the following:\\par\r\n        * a short bio of yourself\\par\r\n        * samples of your work or review copies of your book(s)\\par\r\n        * preference regarding appearance dates\\par\r\n\\par\r\nKeep in mind that the Writers'' Series is booked at least six months in advance.\\par\r\n\\par\r\nSend material to:\\par\r\n\\par\r\nWriters On The Edge, Inc.\\par\r\nP.O. Box 85\\par\r\nNewport, OR 97365\\par\r\n\\par\r\ninfo@writersontheedge.org\\fs17\\par\r\n}\r\n', '2010-12-26 00:00:00');
