INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (177, 'The Lindenwood Review', '', '', '', 'St. Charles', 'MO', '', 'www.lindenwood.edu/lindenwoodReview/', 'TheLindenwoodReview@lindenwood.edu', 10, 2, '2013-12-15 00:00:00', NULL, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 The Lindenwood Review is the literary journal of Lindenwood University in St. Charles, Missouri, produced by the university''s MFA in Writing Program. The journal is published annually each spring; fiction, poetry, and personal essays may be submitted from July 15 through December 15. Submissions are welcome from both new and established writers. We look for fiction with believable characters and a vivid story; poetry with original, interesting use of language; well-crafted, honest essays; and mostly, work that moves us.\\fs17\\par\r\n}\r\n', '2011-11-28 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (178, 'Burnt District', '', '', '', '', '', '', 'www.burntdistrict.org/', '', 40, 1, '2011-11-29 00:00:00', 1, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Tahoma;}{\\f1\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 Submissions\\par\r\nPlease submit up to 5 previously unpublished poems through our online submission manager. Hard copy and e-mailed submissions will not be considered. Simultaneous submissions are welcome, but please notify us immediately when your work is accepted elsewhere. Include complete contact information on every page of your submission. A cover letter is appreciated but not required.\\par\r\n\\par\r\nWe have no restrictions as to form and content. Contributors will receive 2 copies of the issue in which their work appears.\\f1\\fs17\\par\r\n}\r\n', '2011-11-29 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (179, 'CutBank', 'University of Montana', 'English Dept, LA 133', '', 'Missoula', 'MT', '59812', 'www.cutbankonline.org', 'editor.cutbank@gmail.com', 40, 1, '2011-12-10 00:00:00', 1, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs28 Mailing address: CutBank / University of Montana / English Dept, LA 133 / Missoula, MT 59812\\par\r\nEmail address: editor.cutbank@gmail.com\\par\r\n\\par\r\nClint Garner, editor\\fs17\\par\r\n}\r\n', '2011-12-09 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (180, 'Atlas', '', '', '', '', '', '', 'www.atlasaarkarts.zoomshare.com/', 'atlasaarkarts@gmail.com', 8, 1, '2011-12-15 00:00:00', NULL, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 Editor Sudeep Sen\\par\r\n\\par\r\nBoard of contributing editors Peter Bradshaw, Kwame Dawes, John F Deane, Donald Hall, Girish Karnad, Christopher Merrill, Les Murray, Naomi Shihab Nye, Ruth Padel, Peter Porter, Fiona Sampson, Shashi Tharoor, Daniel Weissbort, John Hartley Williams.\\par\r\n\\par\r\nEditorial assistant Madeline Haas\\par\r\n\\par\r\nAtlas - an international paperback magazine of \\lquote new writing, art & image\\rquote  - is published by Aark Arts [UK] & Crossword [India], and advised by an impressive panel of internationally acclaimed prize-winning writers & editors. Available simultaneously from New Delhi, Mumbai, London, Leeds, Toronto, Cambridge & San Antonio, it was launched in the UK/India in the summer/monsoon of 2006. Apart from carrying cutting-edge original and translated creative writing - poetry, drama, fiction & non-fiction, plus occasional in-depth interviews & features - it also includes selected portfolios of artists, photographers & filmmakers.\\par\r\n\\par\r\nAtlas primarily publishes solicited/commissioned works, however it welcomes quality unsolicited submissions. Contributors are invited to be part of the \\lquote Aark Arts International Literary Reading Series\\rquote . \\par\r\n\\par\r\nQueries & submissions Contact Ms Haas at: atlasaarkarts@gmail.com\\fs17\\par\r\n}\r\n', '2011-12-14 00:00:00');
