INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (190, 'Blackbird', 'Virginia Commonwealth University Department of English', 'PO Box 843082', '', 'Richmond', 'VA', '23284-3082', 'www.blackbird.vcu.edu/', 'blackbird@vcu.edu', 264, 2, '2015-03-02 00:00:00', 2, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17 If you have submitted work to us, expect at least six months for a decision. Do not query us until six months have passed, and if you do, please write to us either by email or by including an SASE instead of calling.\\par\r\n}\r\n', '2012-04-29 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (191, 'Generations Literary Journal', '', '', '', '', '', '', 'www.generationslitmag.org', '', 168, 1, '2012-05-13 00:00:00', 1, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17\\par\r\n}\r\n', '2012-05-13 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (192, 'Alaska Quarterly Review', 'Poetry Editor', 'University of Alaska Anchorage', '3211 Providence Drive (ESH 208)', 'Anchorage', 'AK', '99508', 'http://www.uaa.alaska.edu/aqr', '', 9, 1, '2012-09-01 00:00:00', NULL, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17 POETRY: Poems in traditional and experimental styles but no light verse (up to 20 pages).\\par\r\n\\par\r\nPlease include the following contact information in your cover letter and/or on your manuscript: mailing address, phone number, and email address if available. All manuscripts must be typed and accompanied by a self-addressed, stamped envelope (SASE). Unless a SASE is enclosed with your submission, you will not hear from us unless we are interested in publishing your manuscript. We try to reply within 6 to 16 weeks.\\par\r\n\\par\r\nAll general correspondence should be addressed to The Editors. Please address submissions to the section editor (e.g. Fiction Editor, Poetry Editor, Non-Fiction Editor, or Drama Editor) at the following address:\\par\r\n\\par\r\nAlaska Quarterly Review\\par\r\nUniversity of Alaska Anchorage\\par\r\n3211 Providence Drive (ESH 208)\\par\r\nAnchorage, AK 99508\\par\r\n\\par\r\nImportant notes:\\par\r\n(1) Unsolicited manuscripts are read between August 15 and May 15.\\par\r\n(2) Although we respond to e-mail queries, we cannot review electronic submissions.\\par\r\n(3) We review simultaneous submissions and request that they be identified as such in the cover letter.\\par\r\n}\r\n', '2012-06-23 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (193, 'Beloit Poetry Journal', 'P.O. Box 151', '', '', 'Farmington', 'M', '04938', 'www.bpj.org', '', 17, 2, '2013-06-18 00:00:00', 2, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17 Limit your submission to five pages unless you are submitting a long poem (yes, we publish them). It\\rquote s helpful for us to see more than a single short poem, so that we get a fuller sense of your work.\\par\r\n\\par\r\nPlease send no more than two manuscripts within a six-month period.\\par\r\n\\par\r\nPut no more than one poem on a page, single-spaced. Include your name and address (postal and email) on every sheet.\\par\r\n\\par\r\nNo cover note is necessary, unless you have something to say to us. We use no contributors'' notes. Our emphasis is on the poem.\\par\r\n\\par\r\nIf you send poems by postal mail, be sure to enclose a self-addressed stamped envelope with adequate postage to return them, or tell us if you don\\rquote t want them returned with our decision. Manuscripts that arrive without SASEs or sufficient postage will be read but not returned.\\par\r\n\\par\r\nIf you don\\rquote t hear from us within a few weeks, that\\rquote s because your manuscript has been screened and is under active consideration. We clear the decks every quarter; please wait to query us until four months have passed. \\par\r\n}\r\n', '2012-06-24 00:00:00');
