INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (211, 'Willow Springs', '501 N Riverpoint Blvd, Ste 425', NULL, NULL, 'Spokane', 'WA', '99202', 'willowsprings.ewu.edu', 'willowspringsewu@gmail.com', 24, 1, '2012-09-10 00:00:00', 3, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17 Willow Springs is published twice a year, in spring and fall. Submissions in all genres are closed between June 1st and August 31st.\\par\r\n\\par\r\nIn most cases, we respond within eight weeks, though we ask that you do not follow up on a submission unless it has been longer than four months since you sent it.\\par\r\n\\par\r\nWe offer two complimentary copies for work we publish. Accepted works will appear in Willow Springs. They may also appear on the Willow Springs website or in promotional material, and, at a later date, in a Willow Springs anthology. Submitted work must be previously unpublished. We accept simultaneous submissions.\\par\r\n}\r\n', '2012-09-10 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (212, 'Science Fiction Poetry Association', '', NULL, NULL, '', NULL, NULL, 'www.sfpoetry.com', '2012SFPAContest@googlemail.com', 10, 1, '2012-09-12 00:00:00', NULL, '{\\rtf1\\fbidis\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Tahoma;}{\\f1\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\ltrpar\\b\\f0\\fs20 Contest Rules:\\b0\\line This year\\rquote s incarnation of the SFPA\\rquote s speculative poetry contest is open to all poets, including non-SFPA-members. Prizes will be awarded for best poem in 3 categories: \\par\r\n\\par\r\nDwarf (poems 1-10 lines)\\par\r\nShort (11-49 lines [prose poems 0-499 words])\\par\r\nLong (50 lines and more [prose 500 words and up]). \\par\r\n\\par\r\nA special prize for the best poem by a non-SFPA-member poet (chosen from entries of any length) will also be awarded. All sub-genres of speculative fiction allowed in any form.\\line\\line\\pard\\ltrpar\\f1\\fs17\\par\r\n}\r\n', '2012-09-12 00:00:00');
INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (213, 'Wild Leaf Press', 'Shelly Fritsch, editor', 'PO Box 5', NULL, 'New Haven', 'CT', '06501', 'www.wildleafpress.com', NULL, 11, 0, NULL, NULL, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs24 Wild Leaf Press aims to bring hardworking but largely unpublished writers into the marketplace. We are planning a move to web-based publication with a new emphasis on contest-based entries. \\par\r\n\\par\r\n\\b 2012 Wild Leaf Short Story Contest\\b0\\par\r\n\\par\r\nPrizes: $1500 first prize, $500 second prize\\par\r\n\\par\r\nContest Guidelines\\par\r\n\\par\r\nEntry Criteria:\\par\r\na.) One story per entry; 4500 words max (approx 15 pp.)\\par\r\nb.) Use 1.5 line spacing, 12 font helvetiva or courier.\\par\r\nEnter via e-mail to:  editor@wildleafpress.com\\par\r\nc.) entry dates: June 30- November 1, 2012\\par\r\nd.) entry fee:  $25 per entry. Pay by credit card in "Online Store" or personal check to Wild Leaf Press, PO Box 5, New Haven, CT 06501 if website is balky...\\par\r\n\\par\r\nNB:  Your Entry Fee goes farther.....                                                       \\par\r\nWild Leaf Press Pledges to:\\par\r\n--acknowledge your entry by e-mail response.\\par\r\n--announce winners by December 23 by e-mail to all entrants.\\par\r\n--after the judging period, provide a substantive e-mail response to your work.\\par\r\n--consider all entries for inclusion in WLP Fiction Annual 2013, pub. date June 2013.\\par\r\n--mail one copy of WLP Fiction Annual 2013 to each entrant.\\par\r\n--make extra copies of WLP Poetry Annual 2013 available to all entrants at 20% off list\\par\r\n--consider one book MS. from any contestant at no extra cost (reg. $15 reading fee)\\par\r\n\\par\r\nJudge:  Stephen Sangirardi, author of Life on The Planet (WLP, 2009) and Monday Afternoon\\par\r\n(Night Publishing, available on amazon) \\par\r\n      \\par\r\nWild Leaf Press is committed to awarding of prizes, regardless of number of entries.Writer retains copyright in pursuit of publishing a short-story collection by him/herself alone but cedes copyright to WLP in multi-author (short story collection) format.\\fs17\\par\r\n}\r\n', '2012-10-06 00:00:00');
