INSERT INTO "publisher" ("id", "name", "address1", "address2", "address3", "city", "state", "zip", "url", "email", "options", "subcount", "lastsub", "credentialid", "notes", "added") VALUES (223, 'The Meadowland Review', '', '', '', '', '', '', 'www.themeadowlandreview.com/', 'submissions@themeadowlandreview.com', 138, 1, '2013-04-21 00:00:00', NULL, '{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n{\\colortbl ;\\red255\\green0\\blue0;}\r\n\\viewkind4\\uc1\\pard\\cf1\\b\\f0\\fs17 FLAKEY.\\cf0\\b0  The Meadowland Review will not accept material that has appeared in other online or print publications. Simultaneous submissions are accepted, but you must notify The Meadowland Review if your work has been accepted by another publication. All work MUST be original.\\par\r\n\\par\r\nWe accept submissions of:\\par\r\n    poetry:  up to five poems, 80 lines maximum per poem\\par\r\n    short short fiction:  up to 6,000 words\\par\r\n    photography and artwork:  up to 3 photographs or pieces\\par\r\n\\par\r\nThe Meadowland Review will claim first rights to all accepted work and the right to archive all work on this website. Rights to all work in The Meadowland Review will revert to the author/photographer upon publication.\\par\r\n\\par\r\nHow to Submit\\par\r\n    Email your submission to submissions@themeadowlandreview.com\\par\r\n    Please indicate either poetry, short fiction, or photography in the subject line\\par\r\n    For poetry and fiction, please paste the text of your submission into the body of the email.  \\par\r\n    Poetry or fiction submissions sent as attachments will not be read\\par\r\n    For photography and artwork, please provide the title of the photo or piece in your email and attach your image.  We accept images only in the following formats:  GIF, JPG, PNG or TIF.   \\par\r\n    Please include your full name, email address and 1-5 line bio with your submission\\par\r\n    Please do not submit more than once during a reading period\\par\r\n\\par\r\n\\par\r\n}\r\n', '2013-04-21 00:00:00');
