INSERT INTO "submissionbatch" ("id", "publisherid", "authorid", "fee", "award", "online", "contest", "locked", "submitted", "response", "iresponse", "responsetype", "notes") VALUES (156, 133, 1, NULL, NULL, '1', '0', '1', '2010-01-26 00:00:00', '2010-03-02 00:00:00', 179401, 1, 'From: Oren Silverman [mailto:oren@umbrellafactorymagazine.com] \r\nSent: Tuesday, March 02, 2010 3:13 PM\r\nTo: Victor David Sandiego\r\nSubject: Re: Umbrella Factory: Poetry submission\r\n\r\n\r\nDear Victor,\r\n\r\nThank you for submitting to Umbrella Factory Magazine, unfortunately we were not able to choose your work for inclusion in the first issue.  If you have any questions, or would like further feedback (honestly, I''m more than happy to respond in greater detail), please don''t hesitate to contact me. \r\n\r\nBest,\r\n\r\nOren \r\n');
