INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (38, E'Roanoke Review', E'English Department', E'Roanoke College', E'', E'Salem', E'VA', E'24153', E'www.roanoke.edu/roanokereview/', E'review@roanoke.edu', 8, 0, NULL, NULL, E'Paul S. Hanstedt, Editor\nEmail : review@roanoke.edu\n\nThe Roanoke Review publishes poetry and short fiction annually. Submissions are accepted from September 1-February 1. We select poems up to 100 lines in length and fiction from 2,000-7,500 words. You may send up to five poems per submission, though we do not accept multiple submissions of fiction. Simultaneous submissions of either genre are fine.\n\nManuscripts will be returned if accompanied by a self-addressed, stamped envelope. A cover letter is preferred.\n\nSample copies are available for $5.00 including postage. Two-year subscriptions are available for $13.00.\n\nPlease allow 10-12 weeks for response.\nCOPYRIGHT INFORMATION\nThe Roanoke Review owns first rights of any materials it publishes.', '2006-06-29 00:00:00');
