INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (57, E'Ohio State University Press', E'180 Pressey Hall', E'1070 Carmack Road', E'', E'Columbus', E'OH', E'43210-1002', E'ohiostatepress.org/Books/Series Pages/Poetry.html', E'', 8, 1, '2007-09-19 00:00:00', NULL, E'{\\rtf1\\ansi\\deff0{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\lang1033\\f0\\fs17 The Journal, the literary magazine of The Ohio State University, selects one full-length manuscript of poetry each year for publication by The Ohio State University Press. In addition to publication under a standard book contract, the winning author receives the Charles B. Wheeler prize of $3,000.\\par\r\n\\par\r\nEntries of at least 48 typed pages of original poetry must be postmarked during the month of September. Entries postmarked later than September 30 will be returned unread. Clear photocopies are acceptable. Your name or other identification should only appear on the cover page.\\par\r\n\\par\r\nManuscripts must be previously unpublished. Some or all of the poems in the collection may have appeared in periodicals, chapbooks, or anthologies, but these must be identified.\\par\r\n\\par\r\nInclude a nonrefundable handling fee of $25.00 (U.S. dollars) with each manuscript (check or money order payable to The Ohio State University). Entrants will receive a one-year subscription (two issues) to The Journal.\\par\r\n\\par\r\nInclude a stamped, self-addressed business-sized envelope so we can notify you of the results. Manuscripts will not be returned.\\par\r\n\\par\r\nIf you wish us to confirm receipt of your manuscript, include a stamped, self-addressed postcard. The winning entry will be announced by the following January 15.\\par\r\n\\par\r\nOSU Press assumes no responsibility for lost or damaged manuscripts.\\par\r\n\\par\r\nMail to:\\par\r\nPoetry Editor\\par\r\nThe Ohio State University Press\\par\r\n180 Pressey Hall\\par\r\n1070 Carmack Road\\par\r\nColumbus OH 43210-1002\\par\r\n}\r\n', '2007-09-06 00:00:00');
