INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (174, E'Consequence Magazine', E'P. O. Box 323', E'', E'', E'Cohasset', E'MA', E'02025-0323', E'www.consequencemagazine.org/', E'Consequence.Mag@gmail.com', 43, 1, '2011-09-08 00:00:00', 1, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 Guidelines:\\par\r\nPlease observe these guidelines carefully.\\par\r\n\\par\r\nThere is no entry fee.\\par\r\nThe poem(s) should address the consequence of war, or social injustice.\\par\r\nPlease submit no more than three poems of any length.\\par\r\n\\par\r\nSubmissions for the contest may be emailed to Consequence.Mag@gmail.com or mailed to: CONSEQUENCE, PO Box 323, Cohasset, MA 02025-0323, Attention Poetry Editor.\\par\r\n\\par\r\nIf you submit multiple poems, each must begin on a separate page.\\par\r\nInclude your name and contact information in a cover letter only. Please do not identify yourself on the page(s) containing your poem.\\par\r\nIn your cover letter include a short biography of no more than 75 words.\\par\r\n\\par\r\nYour submission should be received by October 1, 2011.\\par\r\nIf you submit by regular mail, and you want confirmation that your entry has been received, please include a self-addressed, stamped post card.\\par\r\nIf you want mailed, original copies returned, include a SASE.\\par\r\n\\par\r\nVisit our website at www.consequencemagazine.org on or after November 25, 2011 to see the winning poet''s work, and the names of the three finalists. Due to the large number of submissions, the announcement on our website will be the only notice of contest results.\\par\r\n\\par\r\nWe look forward to reading your poems. \\fs17\\par\r\n}\r\n', '2011-09-08 00:00:00');
