INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (177, E'The Lindenwood Review', E'', E'', E'', E'St. Charles', E'MO', E'', E'www.lindenwood.edu/lindenwoodReview/', E'TheLindenwoodReview@lindenwood.edu', 10, 2, '2013-12-15 00:00:00', NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 The Lindenwood Review is the literary journal of Lindenwood University in St. Charles, Missouri, produced by the university''s MFA in Writing Program. The journal is published annually each spring; fiction, poetry, and personal essays may be submitted from July 15 through December 15. Submissions are welcome from both new and established writers. We look for fiction with believable characters and a vivid story; poetry with original, interesting use of language; well-crafted, honest essays; and mostly, work that moves us.\\fs17\\par\r\n}\r\n', '2011-11-28 00:00:00');
INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (178, E'Burnt District', E'', E'', E'', E'', E'', E'', E'www.burntdistrict.org/', E'', 40, 1, '2011-11-29 00:00:00', 1, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Tahoma;}{\\f1\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 Submissions\\par\r\nPlease submit up to 5 previously unpublished poems through our online submission manager. Hard copy and e-mailed submissions will not be considered. Simultaneous submissions are welcome, but please notify us immediately when your work is accepted elsewhere. Include complete contact information on every page of your submission. A cover letter is appreciated but not required.\\par\r\n\\par\r\nWe have no restrictions as to form and content. Contributors will receive 2 copies of the issue in which their work appears.\\f1\\fs17\\par\r\n}\r\n', '2011-11-29 00:00:00');
