INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (180, E'Atlas', E'', E'', E'', E'', E'', E'', E'www.atlasaarkarts.zoomshare.com/', E'atlasaarkarts@gmail.com', 8, 1, '2011-12-15 00:00:00', NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 Editor Sudeep Sen\\par\r\n\\par\r\nBoard of contributing editors Peter Bradshaw, Kwame Dawes, John F Deane, Donald Hall, Girish Karnad, Christopher Merrill, Les Murray, Naomi Shihab Nye, Ruth Padel, Peter Porter, Fiona Sampson, Shashi Tharoor, Daniel Weissbort, John Hartley Williams.\\par\r\n\\par\r\nEditorial assistant Madeline Haas\\par\r\n\\par\r\nAtlas - an international paperback magazine of \\lquote new writing, art & image\\rquote  - is published by Aark Arts [UK] & Crossword [India], and advised by an impressive panel of internationally acclaimed prize-winning writers & editors. Available simultaneously from New Delhi, Mumbai, London, Leeds, Toronto, Cambridge & San Antonio, it was launched in the UK/India in the summer/monsoon of 2006. Apart from carrying cutting-edge original and translated creative writing - poetry, drama, fiction & non-fiction, plus occasional in-depth interviews & features - it also includes selected portfolios of artists, photographers & filmmakers.\\par\r\n\\par\r\nAtlas primarily publishes solicited/commissioned works, however it welcomes quality unsolicited submissions. Contributors are invited to be part of the \\lquote Aark Arts International Literary Reading Series\\rquote . \\par\r\n\\par\r\nQueries & submissions Contact Ms Haas at: atlasaarkarts@gmail.com\\fs17\\par\r\n}\r\n', '2011-12-14 00:00:00');
