INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (183, E'LQQK (Defunct)', E'Editor: Phil Klein', E'Phil Klein <phil@lqqkzine.com>', E'', E'', E'', E'', E'www.lqqkzine.com', E'submissions@lqqkzine.com', 138, 2, '2012-09-17 00:00:00', NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 We are a new kind of science fiction magazine currently looking for new writers. We are interested in speculative stories about the future of contemporary phenomena like social networking, mobile devices, filesharing, and online lifestyles. We are also interested in far-out, surrealist, and anarchic stories in general, with or without lulz.\\par\r\n\\par\r\nPlease send submissions as email attachments to address below. We accept the following document formats: Microsoft Word .doc and .docx, .pdf, .rtf, .txt, and .odt. You could also paste the text into the body of the email. Please don''t use any fancy fonts or formatting. Single-spaced text with double-spaced, non-tabbed paragraph breaks preferred ("web style"). Nothing over 10,000 words, please. If you would like to query about a potential story first, feel free. \\fs17\\par\r\n}\r\n', '2011-12-15 00:00:00');
