INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (188, E'Oklahoma Review', E'', NULL, NULL, NULL, NULL, NULL, E'www.cameron.edu/okreview/', E'okreview@cameron.edu', 10, 0, NULL, NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs20 The Oklahoma Review is a continuous publication, now in its eleventh year. We publish two issues online each year: Spring (May) and Fall (December).\\par\r\n\\par\r\nIn the past we have accepted submissions at any time during the year. We will continue to do so, with the following policy changes:\\par\r\n\\par\r\n    * Reading dates for the Fall issue will now be from August 1 to November 15;\\par\r\n    * Reading dates for the Spring issue will be January 5 to April 15.\\par\r\n\\par\r\nWriters may continue to submit their works at any time; however, nobody will be available to read them outside the above dates. We appreciate your cooperation with this change.\\par\r\n\\fs17\\par\r\n}\r\n', '2012-04-02 00:00:00');
