INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (192, E'Alaska Quarterly Review', E'Poetry Editor', E'University of Alaska Anchorage', E'3211 Providence Drive (ESH 208)', E'Anchorage', E'AK', E'99508', E'http://www.uaa.alaska.edu/aqr', E'', 9, 1, '2012-09-01 00:00:00', NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17 POETRY: Poems in traditional and experimental styles but no light verse (up to 20 pages).\\par\r\n\\par\r\nPlease include the following contact information in your cover letter and/or on your manuscript: mailing address, phone number, and email address if available. All manuscripts must be typed and accompanied by a self-addressed, stamped envelope (SASE). Unless a SASE is enclosed with your submission, you will not hear from us unless we are interested in publishing your manuscript. We try to reply within 6 to 16 weeks.\\par\r\n\\par\r\nAll general correspondence should be addressed to The Editors. Please address submissions to the section editor (e.g. Fiction Editor, Poetry Editor, Non-Fiction Editor, or Drama Editor) at the following address:\\par\r\n\\par\r\nAlaska Quarterly Review\\par\r\nUniversity of Alaska Anchorage\\par\r\n3211 Providence Drive (ESH 208)\\par\r\nAnchorage, AK 99508\\par\r\n\\par\r\nImportant notes:\\par\r\n(1) Unsolicited manuscripts are read between August 15 and May 15.\\par\r\n(2) Although we respond to e-mail queries, we cannot review electronic submissions.\\par\r\n(3) We review simultaneous submissions and request that they be identified as such in the cover letter.\\par\r\n}\r\n', '2012-06-23 00:00:00');
INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (193, E'Beloit Poetry Journal', E'P.O. Box 151', E'', E'', E'Farmington', E'ME', E'04938', E'www.bpj.org', E'', 17, 2, '2013-06-18 00:00:00', 2, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17 Limit your submission to five pages unless you are submitting a long poem (yes, we publish them). It\\rquote s helpful for us to see more than a single short poem, so that we get a fuller sense of your work.\\par\r\n\\par\r\nPlease send no more than two manuscripts within a six-month period.\\par\r\n\\par\r\nPut no more than one poem on a page, single-spaced. Include your name and address (postal and email) on every sheet.\\par\r\n\\par\r\nNo cover note is necessary, unless you have something to say to us. We use no contributors'' notes. Our emphasis is on the poem.\\par\r\n\\par\r\nIf you send poems by postal mail, be sure to enclose a self-addressed stamped envelope with adequate postage to return them, or tell us if you don\\rquote t want them returned with our decision. Manuscripts that arrive without SASEs or sufficient postage will be read but not returned.\\par\r\n\\par\r\nIf you don\\rquote t hear from us within a few weeks, that\\rquote s because your manuscript has been screened and is under active consideration. We clear the decks every quarter; please wait to query us until four months have passed. \\par\r\n}\r\n', '2012-06-24 00:00:00');
