INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (195, E'The Emerson Review', E'', NULL, NULL, NULL, NULL, NULL, E'pages.emerson.edu/organizations/emerson_review', NULL, 40, 1, '2012-09-03 00:00:00', NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs17 The Emerson Review accepts submissions from anyone, anywhere! Our submission period runs from August 1st to February 1st for our annual issue in April. \\par\r\n\\par\r\nWe accept all kinds of poems, but have a special place in our hearts for narrative poetry, prose poetry, and form poetry with a contemporary twist. Kick us in the face! Please do not submit more than three poems at a time. Please upload all poems in the same document. \\par\r\n}\r\n', '2012-06-25 00:00:00');
INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (196, E'Harvard Review', E'The Editors', E'Lamont Library', E'Harvard University', E'Cambridge', E'MA', E'02138', E'hcl.harvard.edu/harvardreview', E'', 13, 1, '2013-09-16 00:00:00', 8, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Calibri;}{\\f1\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n{\\colortbl ;\\red139\\green0\\blue0;}\r\n\\viewkind4\\uc1\\pard\\f0\\fs32 Uses \\cf1\\i Tell It Slant\\cf0\\i0  for submissions\\f1\\fs17\\par\r\n\\par\r\nThe Editors\\par\r\nHarvard Review\\par\r\nLamont Library\\par\r\nHarvard University\\par\r\nCambridge, MA 02138\\par\r\n}\r\n', '2012-06-25 00:00:00');
INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (197, E'Stone Highway Review', NULL, NULL, NULL, NULL, NULL, NULL, E'www.stonehighway.com', NULL, 40, 0, NULL, NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n\\viewkind4\\uc1\\pard\\f0\\fs18 Simply put, we are looking for amazing poetry that is moves us through its specific language, use of images, and pays attention to each individual line. We want poems that linger, that leave us in wonder. Great poetry should make you have to catch your breath after reading it and that''s exactly what we''re wanting to find. We prefer shorter works (2 pages or less) but will consider longer pieces.\\par\r\n\\par\r\nBEFORE SUBMITTING: We prefer poetry that focuses on the specifics of life rather than poetry that ponders big, philosophical ideals. We want imagery rather than rant. Be sure your work is right for us and send us only the best. PLEASE SUBMIT MULTIPLE POEMS IN ONE DOCUMENT - UP TO 7 POEMS.\\fs17\\par\r\n}\r\n', '2012-06-25 00:00:00');
INSERT INTO "publication" ("publicationid", "pubname", "address1", "address2", "address3", "city", "state", "zip", "website", "email", "options", "submission_count", "last_submission_date", "credential_id", "notes", "date_added") VALUES (198, E'Strange Horizons', E'', NULL, NULL, NULL, NULL, NULL, E'www.strangehorizons.com', E'poetry@strangehorizons.com', 2, 2, '2012-06-25 00:00:00', NULL, E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n{\\colortbl ;\\red139\\green0\\blue0;}\r\n\\viewkind4\\uc1\\pard\\cf1\\b\\f0\\fs20 How to submit\\par\r\n\\cf0\\b0\\par\r\nEmail poetry to poetry@strangehorizons.com. Please submit no more than six poems per month, one poem per email. For each poem, type "POETRY SUB: Your poem title" in the subject line. It is very important that you follow this format. If you do not, your submission may be tagged as spam by our email systems, and we will not see it.\\par\r\n\\par\r\nPoems should be in plain text (ASCII) format in the body of your email message, not an attachment. (One way to do this: use your word processor''s Save As Text command to save the poem, then copy the resulting text and paste it into an email message.) Use two line breaks (double spacing) to indicate paragraph breaks. Place an _underscore_ at the beginning and end of a word or phrase to indicate italics; use *asterisks* to indicate boldface. Please do not put submissions or poetry titles in ALL CAPS. For any other special formatting, please include an explanatory note. Submissions which are not properly formatted may not be read.\\fs17\\par\r\n}\r\n', '2012-06-25 00:00:00');
