INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (54, E'00000000FC0A50B868F35745AE9D2EED70629592E4072000', 231, E'0', E'Generations Literary Journal', E'editor@generationslitmag.org', E'Victor David Sandiego', E'99poets@victordavid.com', E'Generations Literary Journal: EDITS for How I Arrived Here', E'Generations Literary Journal: EDITS for How I Arrived Here', '2012-07-17 20:03:46', '2012-07-17 20:03:48', 2, E'<style>\r\n<!--\r\n /* Font Definitions */\r\n@font-face\r\n\t{font-family:"ＭＳ 明朝";\r\n\tmso-font-charset:78;\r\n\tmso-generic-font-family:auto;\r\n\tmso-font-pitch:variable;\r\n\tmso-font-signature:-536870145 1791491579 18 0 131231 0;}\r\n@font-face\r\n\t{font-family:"Cambria Math";\r\n\tpanose-1:2 4 5 3 5 4 6 3 2 4;\r\n\tmso-font-charset:0;\r\n\tmso-generic-font-family:auto;\r\n\tmso-font-pitch:variable;\r\n\tmso-font-signature:-536870145 1107305727 0 0 415 0;}\r\n@font-face\r\n\t{font-family:Cambria;\r\n\tpanose-1:2 4 5 3 5 4 6 3 2 4;\r\n\tmso-font-charset:0;\r\n\tmso-generic-font-family:auto;\r\n\tmso-font-pitch:variable;\r\n\tmso-font-signature:-536870145 1073743103 0 0 415 0;}\r\n /* Style Definitions */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal\r\n\t{mso-style-unhide:no;\r\n\tmso-style-qformat:yes;\r\n\tmso-style-parent:"";\r\n\tmargin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tmso-pagination:widow-orphan;\r\n\tfont-size:12.0pt;\r\n\tfont-family:Cambria;\r\n\tmso-ascii-font-family:Cambria;\r\n\tmso-ascii-theme-font:minor-latin;\r\n\tmso-fareast-font-family:"ＭＳ 明朝";\r\n\tmso-fareast-theme-font:minor-fareast;\r\n\tmso-hansi-font-family:Cambria;\r\n\tmso-hansi-theme-font:minor-latin;\r\n\tmso-bidi-font-family:"Times New Roman";\r\n\tmso-bidi-theme-font:minor-bidi;}\r\n.MsoChpDefault\r\n\t{mso-style-type:export-only;\r\n\tmso-default-props:yes;\r\n\tfont-family:Cambria;\r\n\tmso-ascii-font-family:Cambria;\r\n\tmso-ascii-theme-font:minor-latin;\r\n\tmso-fareast-font-family:"ＭＳ 明朝";\r\n\tmso-fareast-theme-font:minor-fareast;\r\n\tmso-hansi-font-family:Cambria;\r\n\tmso-hansi-theme-font:minor-latin;\r\n\tmso-bidi-font-family:"Times New Roman";\r\n\tmso-bidi-theme-font:minor-bidi;}\r\n@page WordSection1\r\n\t{size:8.5in 11.0in;\r\n\tmargin:1.0in 1.25in 1.0in 1.25in;\r\n\tmso-header-margin:.5in;\r\n\tmso-footer-margin:.5in;\r\n\tmso-paper-source:0;}\r\ndiv.WordSection1\r\n\t{page:WordSection1;}\r\n-->\r\n</style>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<p class="MsoNormal"><span style>Hi Victor,<br>\r\n<br>\r\nPlease review the suggested edits for your piece, [How I Arrived Here]. Mostly, we need you to approve the line breaks. Because of space limitations, your lines cannot be the width of a standard piece of paper, so we&#39;ve adjusted to fit our page size. <br>\r\n\r\n<br>\r\nIf you agree with the edits as they appear in the attached file, please\r\nindicate this by replying YES to this email. Your yes reply will confirm that\r\nwe can move forward with publishing the edited version of your submission. <br>\r\n<br>\r\nIf you have questions, please reply to this email to keep the thread intact. <br>\r\n<br>\r\nIf you disagree with the edits and would rather we did not publish your piece,\r\nplease let us know that immediately. </span></p>\r\n\r\n<p class="MsoNormal"><span style> </span></p>\r\n\r\n<p class="MsoNormal"><span style>Please be sure to check the spelling of\r\nyour name as well. <br>\r\n<br>\r\n<b>Please reply by midnight (PST) Friday July 20, 2012. </b><br>\r\n<br>\r\nWe look forward to working with you as we continue the process. <br>\r\n<br>\r\nAll the best, <br>\r\nThe Editors</span></p>\r\n\r\n\r\n\r\n\r\n\r\n<br clear="all"><br>-- <br>-------------------------------------<br>\r\n<a href="http://www.generationslitmag.org" target="_blank">Generations Literary Journal</a><br><br>Twitter: <a href="http://twitter.com/generationslit" target="_blank">@generationslit</a><br>Visit Our <a href="https://www.facebook.com/GenerationsLitJournal" target="_blank">Facebook</a> Page <br>\r\n<br>');
