INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (62, E'00000000FC0A50B868F35745AE9D2EED7062959244012000', 224, E'0', E'Mid-American Review', E'do-not-reply@bgsu.edu', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Thank you for your submission to Mid-American Review', E'Thank you for your submission to Mid-American Review', '2012-01-19 17:21:30', '2012-01-19 17:21:33', 1, E'Dear Victor David Sandiego,\r\n\r\nYour submission was received successfully.\r\n\r\nVictor David Sandiego\r\n99poets@victordavid.com\r\nCallejon Carrica #4\r\nGuanajuato\r\nMexico\r\n\r\nwriter: Victor David Sandiego\r\ntitle(s): 5 poems\r\ngenre: poetry\r\n\r\nThank you for your submission to Mid-American Review! You may check the status of your submission at any time by visiting http://marsubmissions.bgsu.edu and logging into your account.\r\n\r\nThe editors of Mid-American Review are writers, too, without exception, so we understand what it means to entrust a magazine with your work, and we promise to treat it respectfully. Note that we are very receptive to simultaneous submissions -- a sensible approach to publishing today. However, we hope that you will abide by the ethical practices of not oversubmitting your work and of withdrawing your work immediately upon acceptance elsewhere. We will be pleased to celebrate your successes with you.\r\n\r\nGood luck!\r\n\r\n2012-01-19 18:21:30 (GMT -5:00)');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (63, E'00000000FC0A50B868F35745AE9D2EED70629592C4032000', 144, E'1', E'Victor David Sandiego', E'poetry2009@victordavidsandiego.com', E'''editor@grasslimb.com''', E'editor@grasslimb.com', E'Submission (Poetry)', E'Submission (Poetry)', '2009-08-17 19:56:59', '2009-08-17 19:56:00', 1, E'Dear Ms. Polichar -\r\n\r\nThank you for the opportunity to submit a few of my poems to Grasslimb Journal for your consideration. The attached MS Word file contains the following poems:\r\n\r\n during the uprising\r\n the last time I turned 31\r\n No work in Guadalajara\r\n Our creation of faces\r\n The streets of messenger\r\n El jefe is down  *\r\n\r\n* = simultaneous submission\r\n\r\nThanks again. Please let me know if you have any questions or need more information.\r\n\r\nSincerely,\r\n\r\nVictor David Sandiego');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (64, E'00000000FC0A50B868F35745AE9D2EED7062959224022000', 183, E'1', E'Victor David Sandiego', E'99poets@victordavidsandiego.com', E'''editor@grasslimb.com''', E'editor@grasslimb.com', E'Submission (Poetry) - Grasslimb', E'Submission (Poetry) - Grasslimb', '2010-09-29 23:12:40', '2010-09-29 23:12:00', 1, E'Dear Valerie -\r\n\r\nThank you for another opportunity to submit a few of my poems to Grasslimb Journal for your consideration. The attached RTF file contains the following poems:\r\n\r\n boy on ground 14\r\n swallow her within me\r\n 10 minute conception\r\n Breaking bread with Muwadi\r\n My inhabited chest\r\n\r\nThanks again. Please let me know if you have any questions or need more information.\r\n\r\nSincerely,\r\n\r\nVictor David Sandiego\r\nwww.VictorDavid.com');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (65, E'00000000FC0A50B868F35745AE9D2EED7062959264022000', 178, E'1', E'Victor David Sandiego', E'99poets@victordavidsandiego.com', E'''rclever@blastfurnacepress.com''', E'rclever@blastfurnacepress.com', E'Submissions to Blast Furnace', E'Submissions to Blast Furnace', '2010-08-14 01:15:33', '2010-08-14 01:15:00', 1, E'Hello Blast Furnace -\r\n\r\nThanks for the opportunity to send you a couple of poems for your consideration:\r\n\r\n  Friday at the factory\r\n  I. VA. Tech. 4.21.2007 plus or minus massacre\r\n\r\nThe first one fits your blue collar theme, I belive. The 2nd does not unless you consider an insane mass murderer of kids* (now dead more than 3 years) an ordinary worker. At any rate, thank you, and best of luck with your inaugural issue.\r\n\r\n\r\nVictor David Sandiego\r\nwww.victordavidsandiego.com\r\n\r\n \r\n*However, we will be happy to consider poetry outside of this theme.');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (66, E'00000000FC0A50B868F35745AE9D2EED7062959244032000', 172, E'1', E'Victor David Sandiego', E'poetry2009@victordavidsandiego.com', E'''redactionspoetry@yahoo.com''', E'redactionspoetry@yahoo.com', E'Poetry Submissions - Redactions', E'Poetry Submissions - Redactions', '2010-06-07 22:07:21', '2010-06-07 22:07:00', 1, E'Dear Redactions -\r\n\r\nI have attached a MS Word document that contains the following poems for your consideration:\r\n\r\nThe Hands of Roman\r\nSanctuary\r\nMuwadi in captivity\r\nEl antílope y el oso\r\n\r\nAs requested, a brief bio:\r\n\r\nVictor David Sandiego divides his time between México and the United States. David was the winner of the 1st WordStorm Poetry Competition held on Vancouver Island in British Columbia and the winner of the 2009 Crab Creek Review poetry contest. His work has appeared (or will appear) in Floating Bridge Review, Crab Creek Review, Cascade, Grasslimb,  Cerise Press, and on public radio. He performs with a musical / poetry collaborative group, Band of Poets. His website is www.VictorDavidSandiego.com.\r\n\r\nThank you. Please let me know if you need more information.\r\n\r\nVictor David Sandiego\r\n6523 California Ave #165\r\nSeattle, WA 98136\r\nwww.VictorDavidSandiego.com');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (67, E'00000000FC0A50B868F35745AE9D2EED7062959264032000', 173, E'1', E'Victor David Sandiego', E'99poets@victordavidsandiego.com', E'''write@protestpoems.org''', E'write@protestpoems.org', E'Poetry Submission - Protest Poems', E'Poetry Submission - Protest Poems', '2010-06-09 15:09:43', '2010-06-09 15:09:00', 1, E'Dear Protest Poems -\r\n\r\nI have attached an RTF file with the following 3 poems for your consideration.\r\n\r\n PROMISES\r\n The body incorporated\r\n 2400 Hours\r\n\r\nAs requested, a brief bio:\r\n\r\nVictor David Sandiego divides his time between México and the United States. David was the winner of the 1st WordStorm Poetry Competition held on Vancouver Island in British Columbia and the winner of the 2009 Crab Creek Review poetry contest. His work has appeared (or will appear) in Floating Bridge Review, Crab Creek Review, Cascade, Grasslimb, Cerise Press, and on public radio. He performs with a musical / poetry collaborative group, Band of Poets. His website is www.VictorDavidSandiego.com.\r\n\r\nThank you. Please let me know if you need more information.\r\n\r\n\r\nVictor David Sandiego \r\nwww.VictorDavidSandiego.com');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (68, E'00000000FC0A50B868F35745AE9D2EED7062959284052000', 230, E'1', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'''editors@slatepoems.com''', E'editors@slatepoems.com', E'Submission to Slate: Victor David Sandiego', E'Submission to Slate: Victor David Sandiego', '2012-04-14 00:00:18', '2012-04-14 00:00:00', 2, E'<html xmlns:v="urn:schemas-microsoft-com:vml" xmlns:o="urn:schemas-microsoft-com:office:office" xmlns:w="urn:schemas-microsoft-com:office:word" xmlns:m="http://schemas.microsoft.com/office/2004/12/omml" xmlns="http://www.w3.org/TR/REC-html40"><head><meta name=Generator content="Microsoft Word 14 (filtered medium)"><style><!--\r\n/* Font Definitions */\r\n@font-face\r\n\t{font-family:Verdana;\r\n\tpanose-1:2 11 6 4 3 5 4 4 2 4;}\r\n/* Style Definitions */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal\r\n\t{margin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:10.0pt;\r\n\tfont-family:"Verdana","sans-serif";}\r\na:link, span.MsoHyperlink\r\n\t{mso-style-priority:99;\r\n\tcolor:blue;\r\n\ttext-decoration:underline;}\r\na:visited, span.MsoHyperlinkFollowed\r\n\t{mso-style-priority:99;\r\n\tcolor:purple;\r\n\ttext-decoration:underline;}\r\nspan.EmailStyle17\r\n\t{mso-style-type:personal-compose;\r\n\tfont-family:"Calibri","sans-serif";\r\n\tcolor:windowtext;}\r\np.Indentedjustified, li.Indentedjustified, div.Indentedjustified\r\n\t{mso-style-name:"Indented \\(justified\\)";\r\n\tmso-style-priority:8;\r\n\tmargin-top:0in;\r\n\tmargin-right:0in;\r\n\tmargin-bottom:0in;\r\n\tmargin-left:9.0pt;\r\n\tmargin-bottom:.0001pt;\r\n\ttext-align:justify;\r\n\tfont-size:10.0pt;\r\n\tfont-family:"Verdana","sans-serif";}\r\nspan.NoSpellCheck\r\n\t{mso-style-name:"No Spell Check";\r\n\tmso-style-priority:9;\r\n\tcolor:maroon;}\r\n.MsoChpDefault\r\n\t{mso-style-type:export-only;\r\n\tfont-family:"Calibri","sans-serif";}\r\n@page WordSection1\r\n\t{size:8.5in 11.0in;\r\n\tmargin:1.0in 1.0in 1.0in 1.0in;}\r\ndiv.WordSection1\r\n\t{page:WordSection1;}\r\n--></style><!--[if gte mso 9]><xml>\r\n<o:shapedefaults v:ext="edit" spidmax="1026" />\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n<o:shapelayout v:ext="edit">\r\n<o:idmap v:ext="edit" data="1" />\r\n</o:shapelayout></xml><![endif]--></head><body lang=EN-US link=blue vlink=purple><div class=WordSection1><p class=MsoNormal>Slate Poetry<o:p></o:p></p><p class=MsoNormal>Robert Pinsky, Editor<o:p></o:p></p><p class=MsoNormal>editors@slatepoems.com<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>Dear Mr. Pinsky,<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>In the attached Word document, I&#8217;ve included three poems for your consideration. The first two are from a longer work titled <i>The Second Book of Muwadi</i> and the third is from another longer work, in progress and as yet unnamed. Please consider all as simultaneous submissions.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>As requested, a brief bio:<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=Indentedjustified>Victor David Sandiego was the winner of the 1st WordStorm Poetry Competition held on Vancouver Island in British Columbia, a winner of the Jeanne Lohmann Poetry Prize, and the winner of the 2009 Crab Creek Review poetry contest. His work appears in various journals (<i>Floating Bridge Review</i>, <i>Crab Creek Review</i>, <i>Cascade, Prime Number Magazine, Qarrtsiluni, </i>and others<i>)</i> as well as on public radio. As baterista and composer, Victor David performs with others in musical / poetry collaborative settings. He lives in the high desert of central México.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>Thank you and please let me know if you need more information.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>Sincerely,<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>Victor David Sandiego<o:p></o:p></p></div></body></html>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (69, E'00000000FC0A50B868F35745AE9D2EED70629592A4012000', 208, E'1', E'Victor David Sandiego', E'vds@victordavid.com', E'''Consequence.Mag@gmail.com''', E'Consequence.Mag@gmail.com', E'Entry: The Consequence Prize in Poetry', E'Entry: The Consequence Prize in Poetry', '2011-09-09 12:26:29', '2011-09-09 12:26:00', 1, E'Dear Consequence Magazine:\r\n\r\nI am submitting the following three poems for the Consequence Prize in Poetry:\r\n\r\nAnniversary \r\nvengeance is a dull knife\r\nSouth of the cave\r\n\r\nThe attached file contains the three poems plus a cover letter with a bio and contact details.\r\n\r\nThank you. Please let me know if you need any more information.\r\n\r\nVictor David Sandiego');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (70, E'00000000FC0A50B868F35745AE9D2EED7062959264012000', 213, E'0', E'Black Warrior Review', E'bwr@sa.ua.edu', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Thank you for your submission to Black Warrior Review', E'Thank you for your submission to Black Warrior Review', '2011-10-28 18:49:24', '2011-10-28 18:49:25', 1, E'Dear Victor David Sandiego,\r\n\r\nYour submission was received successfully.\r\n\r\nVictor David Sandiego\r\n99poets@victordavid.com\r\nCallejon Carrica #4\r\nGuanajuato\r\nMexico\r\n\r\nwriter: Victor David Sandiego\r\ntitle(s): 5 poems from: 57 Women of the Earth\r\ngenre: poetry\r\n\r\nThank you for submitting your work. You can check the status of your submission at any time by visiting (http://bwrsubmissions.ua.edu/submissions) and logging into your account. Our response times are between 3 and 5 months, so expect a response by that point.\r\n\r\nIf for some reason this doesn''t work, you can manually query us by emailing blackwarriorreview@gmail.com and specifying your last name and the submission date, and we''ll let you know its status as soon as possible. \r\n\r\nNote that our Submission Manager does not allow you to withdraw individual poems, for instance, from a submission. In the case that a submission is accepted elsewhere, you may either withdraw the whole submission and then resubmit the remaining available works, or else email the update to blackwarriorreview@gmail.com.\r\n\r\nIMPORTANT: If you are submitting a contest entry, please make sure you have submitted your online payment. If you did not do this when you submitted your work, you can do so at the OSMstore\r\n\r\nYou will then receive a payment reference number. You will then need to email this number to blackwarriorreview@gmail.com; please title your email "Contest Payment Confirmation, [Last Name], [First Name]." \r\n\r\nThanks again for submitting to Black Warrior Review. We look forward to reading your work.\r\n\r\n--The Editors\r\n\r\n2011-10-28 18:49:24 (GMT -5:00)');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (71, E'00000000FC0A50B868F35745AE9D2EED7062959224032000', 172, E'0', E'Re Dactions', E'redactionspoetry@yahoo.com', E'Victor David Sandiego', E'poetry2009@victordavidsandiego.com', E'Your submissions', E'Your submissions', '2010-06-23 19:45:19', '2010-06-23 19:51:13', 2, E'<html><head><style type="text/css"><!-- DIV {margin:0px;} --></style></head><body><div style="font-family:times new roman, new york, times, serif;font-size:12pt"><DIV></DIV>\r\n<DIV>Victor,</DIV>\r\n<DIV>&nbsp;</DIV>\r\n<DIV>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-bidi-font-size: 10.0pt">Thank you for your submission to <I>Redactions</I>. Your poems were read with care. Unfortunately we will not be able to use any of the poems for our next issue.<\x3fxml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-bidi-font-size: 10.0pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-bidi-font-size: 10.0pt">Please feel free to submit again.<o:p></o:p></SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-bidi-font-size: 10.0pt"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-bidi-font-size: 10.0pt">All the best,<o:p></o:p></SPAN></P>\r\n<P style="MARGIN: 0in 0in 0pt" class=MsoNormal><SPAN style="mso-bidi-font-size: 10.0pt">Tom Holmes<o:p></o:p></SPAN></P><SPAN style="FONT-FAMILY: ''Times New Roman'',''serif''; FONT-SIZE: 12pt; mso-bidi-font-size: 10.0pt; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA">Editor</SPAN><BR>&nbsp;</DIV>\r\n<DIV><EM><FONT color=#ff0000><STRONG>Redactions: Poetry &amp; Poetics</STRONG></FONT></EM> <BR><A href="http://www.redactions.com/" rel=nofollow target=_blank>www.redactions.com</A>&nbsp;<BR><A href="mailto:redactionspoetry@yahoo.com" rel=nofollow target=_blank>redactionspoetry@yahoo.com</A></DIV>\r\n<DIV><BR>For every poetic action there is . . . <FONT color=#ff0000><STRONG>Redactions</STRONG></FONT>.</DIV>\r\n<DIV><BR></DIV>\r\n<DIV style="FONT-FAMILY: times new roman, new york, times, serif; FONT-SIZE: 12pt"><BR>\r\n<DIV style="FONT-FAMILY: arial, helvetica, sans-serif; FONT-SIZE: 13px"><FONT size=2 face=Tahoma>\r\n<HR SIZE=1>\r\n<B><SPAN style="FONT-WEIGHT: bold">From:</SPAN></B> Victor David Sandiego &lt;poetry2009@victordavidsandiego.com&gt;<BR><B><SPAN style="FONT-WEIGHT: bold">To:</SPAN></B> redactionspoetry@yahoo.com<BR><B><SPAN style="FONT-WEIGHT: bold">Sent:</SPAN></B> Mon, June 7, 2010 11:07:20 PM<BR><B><SPAN style="FONT-WEIGHT: bold">Subject:</SPAN></B> Poetry submissions<BR></FONT><BR>Dear Redactions -<BR><BR>I have attached a MS Word document that contains the following poems for<BR>your consideration:<BR><BR>The Hands of Roman<BR>Sanctuary<BR>Muwadi in captivity<BR>El antílope y el oso<BR><BR>As requested, a brief bio:<BR><BR>Victor David Sandiego divides his time between México and the United States.<BR>David was the winner of the 1st WordStorm Poetry Competition held on<BR>Vancouver Island in British Columbia and the winner of the 2009 Crab Creek<BR>Review poetry contest. His work has appeared (or will appear) in Floating<BR>Bridge Review, Crab Creek Review,\r\n Cascade, Grasslimb,&nbsp; Cerise Press, and on<BR>public radio. He performs with a musical / poetry collaborative group, Band<BR>of Poets. His website is <A href="http://www.victordavidsandiego.com/" target=_blank>www.VictorDavidSandiego.com</A>.<BR><BR>Thank you. Please let me know if you need more information.<BR><BR>Victor David Sandiego<BR>6523 California Ave #165<BR>Seattle, WA 98136<BR>www.VictorDavidSandiego.com<BR></DIV></DIV></div><br>\r\n\r\n      </body></html>');
