INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (103, E'00000000FC0A50B868F35745AE9D2EED70629592A4182000', 296, E'0', E'Tell It Slant', E'no-reply@tellitslant.com', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Raleigh Review: Received from Tell It Slant', E'Raleigh Review: Received from Tell It Slant', '2013-09-16 17:35:03', '2013-09-16 17:35:03', 2, E'Dear Victor David Sandiego, <br/>\r\n<p>Raleigh Review is now considering your manuscript(s), <i> Submissions to Raleigh Review </i>, which they received on September 16, 2013 18:35. The biography in your profile will also be available to them.\r\nOnce the editors at Raleigh Review reach a decision about your submission, you will receive an email with their response from Tell It Slant and your My Submissions page will be\r\nupdated to reflect their findings. You can, however, check your account at anytime, after logging in: http://www.tellitslant.com/ </p>\r\n\r\n<br/>\r\n<p>Thank you for submitting your work through Tell It Slant.</p>\r\n<br/>\r\nBest,<br/>\r\nThe delivery people at Tell It Slant<br/>\r\n<br/>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (104, E'00000000FC0A50B868F35745AE9D2EED7062959224192000', 296, E'0', E'Tell It Slant', E'noreply@tellitslant.com', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Raleigh Review: Reply', E'Raleigh Review: Reply', '2013-11-02 14:58:17', '2013-11-02 14:58:18', 2, E'<p>Thank you for submitting your poems to <i>Raleigh Review</i>. Unfortunately, we will not be able to use them at this time. However, we would like you to know that each of your poems has been carefully read by at least three members of the staff.&nbsp; We recognize the value of every poem sent our way and sincerely appreciate you thinking of <i>Raleigh Review</i>.<i> </i>Please submit again. Many of our published poems come from those who have submitted several times.</p>\r\n<p>Best,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n<p>Sierra and <i>Raleigh Review</i> Staff</p>\r\n<p>&nbsp;</p>\r\n<p>&ldquo;Man and stone need every hour</p>\r\n<p>of darkness to exhale the sun&rsquo;s fire.&rdquo;</p>\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; --Jeffery Alfier, from &ldquo;Terlingua&rdquo;</p>\r\n<p><i>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Raleigh Review</i>, vol. 3</p>\r\n<p>&nbsp;</p>\r\n<p>Please download the document located here : https://www.tellitslant.com/attachments/original/missing.png for more information</p>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (105, E'00000000FC0A50B868F35745AE9D2EED7062959244152000', 274, E'0', E'Off the Coast', E'poetrylane2@gmail.com', E'Victor David Sandiego', E'99poets@victordavid.com', E'RE: [Off the Coast] Confirmation', E'RE: [Off the Coast] Confirmation', '2013-07-07 13:19:15', '2013-07-07 13:19:16', 2, E'Dear Victor David,\r\n<br />\r\n<br />As previously stated in an email, we are delighted to have accepted your poem The Dark Side of Devotion for publication in the Summer 2013 issue of Off the Coast. \r\n<br />\r\n<br />This notification will facilitate record keeping via Submittable.\r\n<br />\r\n<br />We hope you enjoy your contributor copy, which should arrive in mid-August.\r\n<br />\r\n<br />\r\n<br />Regards,\r\n<br />The Editors\r\n<br />Off the Coast\r\n<br />http://www.off-the-coast.com<img width="1px" height="1px" src="http://email.email.submittable.com/o/aD03OTA2YmFjNTg5YWYzODQ3YzZlZTBjMmVkNWM2YmQzMiZyPTk5cG9ldHMlNDB2aWN0b3JkYXZpZC5jb20mdD1hY2NlcHRlZCZkPWQ3ZDU5">');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (106, E'00000000FC0A50B868F35745AE9D2EED7062959224152000', 274, E'1', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'''Off the Coast''', E'poetrylane2@gmail.com', E'RE: [Off the Coast] RE: dark side of devotion', E'RE: [Off the Coast] RE: dark side of devotion', '2013-07-07 10:26:33', '2013-07-07 10:17:00', 2, E'<html xmlns:v="urn:schemas-microsoft-com:vml" xmlns:o="urn:schemas-microsoft-com:office:office" xmlns:w="urn:schemas-microsoft-com:office:word" xmlns:m="http://schemas.microsoft.com/office/2004/12/omml" xmlns="http://www.w3.org/TR/REC-html40"><head><meta name=Generator content="Microsoft Word 14 (filtered medium)"><!--[if !mso]><style>v\\:* {behavior:url(#default#VML);}\r\no\\:* {behavior:url(#default#VML);}\r\nw\\:* {behavior:url(#default#VML);}\r\n.shape {behavior:url(#default#VML);}\r\n</style><![endif]--><style><!--\r\n/* Font Definitions */\r\n@font-face\r\n\t{font-family:Calibri;\r\n\tpanose-1:2 15 5 2 2 2 4 3 2 4;}\r\n@font-face\r\n\t{font-family:Tahoma;\r\n\tpanose-1:2 11 6 4 3 5 4 4 2 4;}\r\n@font-face\r\n\t{font-family:Verdana;\r\n\tpanose-1:2 11 6 4 3 5 4 4 2 4;}\r\n/* Style Definitions */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal\r\n\t{margin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:12.0pt;\r\n\tfont-family:"Times New Roman","serif";}\r\nh2\r\n\t{mso-style-priority:1;\r\n\tmso-style-link:"Heading 2 Char";\r\n\tmargin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tpage-break-after:avoid;\r\n\tfont-size:11.0pt;\r\n\tfont-family:"Verdana","sans-serif";}\r\na:link, span.MsoHyperlink\r\n\t{mso-style-priority:99;\r\n\tcolor:blue;\r\n\ttext-decoration:underline;}\r\na:visited, span.MsoHyperlinkFollowed\r\n\t{mso-style-priority:99;\r\n\tcolor:purple;\r\n\ttext-decoration:underline;}\r\np.MsoAcetate, li.MsoAcetate, div.MsoAcetate\r\n\t{mso-style-priority:99;\r\n\tmso-style-link:"Balloon Text Char";\r\n\tmargin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:8.0pt;\r\n\tfont-family:"Tahoma","sans-serif";}\r\nspan.EmailStyle17\r\n\t{mso-style-type:personal-reply;\r\n\tfont-family:"Verdana","sans-serif";\r\n\tcolor:black;}\r\nspan.BalloonTextChar\r\n\t{mso-style-name:"Balloon Text Char";\r\n\tmso-style-priority:99;\r\n\tmso-style-link:"Balloon Text";\r\n\tfont-family:"Tahoma","sans-serif";}\r\nspan.Heading2Char\r\n\t{mso-style-name:"Heading 2 Char";\r\n\tmso-style-priority:1;\r\n\tmso-style-link:"Heading 2";\r\n\tfont-family:"Verdana","sans-serif";\r\n\tfont-weight:bold;}\r\np.CharacterVoice, li.CharacterVoice, div.CharacterVoice\r\n\t{mso-style-name:"Character Voice";\r\n\tmso-style-priority:2;\r\n\tmargin-top:0in;\r\n\tmargin-right:0in;\r\n\tmargin-bottom:0in;\r\n\tmargin-left:9.0pt;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:10.0pt;\r\n\tfont-family:"Verdana","sans-serif";\r\n\tfont-style:italic;}\r\n.MsoChpDefault\r\n\t{mso-style-type:export-only;\r\n\tfont-family:"Calibri","sans-serif";}\r\n@page WordSection1\r\n\t{size:8.5in 11.0in;\r\n\tmargin:1.0in 1.0in 1.0in 1.0in;}\r\ndiv.WordSection1\r\n\t{page:WordSection1;}\r\n--></style><!--[if gte mso 9]><xml>\r\n<o:shapedefaults v:ext="edit" spidmax="1026" />\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n<o:shapelayout v:ext="edit">\r\n<o:idmap v:ext="edit" data="1" />\r\n</o:shapelayout></xml><![endif]--></head><body lang=EN-US link=blue vlink=purple><div class=WordSection1><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''>Dear Valerie and Michael –<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''>I hope you got my note I left via the submission manager a few days ago. Although delayed somewhat because of the volcano, I’m back home now. The one formatting thing that didn’t come through is that the fourth stanza should be indented and italicized. I’ve pasted the piece in below which should preserve the formatting.<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''>Once more, thanks. I’m pleased to find such a nice home in Off The Coast for this piece. Please let me know if you have any questions.<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''>Victor David<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><h2>The dark side of devotion<o:p></o:p></h2><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>We are a peaceful garden people<o:p></o:p></p><p class=MsoNormal>under an eggshell moon – ancestral sins<o:p></o:p></p><p class=MsoNormal>mostly scraped from our skin.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>Our terrible fathers<o:p></o:p></p><p class=MsoNormal>buried in shadow – their crimes<o:p></o:p></p><p class=MsoNormal>covered in salt.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>We do not fault<o:p></o:p></p><p class=MsoNormal>they carved their pride with iron<o:p></o:p></p><p class=MsoNormal>bars into stone.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=CharacterVoice>A child in the sand of the garden waits<o:p></o:p></p><p class=CharacterVoice>for a rose drop of dew<o:p></o:p></p><p class=CharacterVoice>to free his sad lips from an innocent bondage.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>Recall: towers of our history<o:p></o:p></p><p class=MsoNormal>still stand<o:p></o:p></p><p class=MsoNormal>in an unclean corner of our land.<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>But these relics of our devotion<o:p></o:p></p><p class=MsoNormal>must someday yield to rubble<o:p></o:p></p><p class=MsoNormal>and we’ll have a dance<o:p></o:p></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>of blood red bursts – of spangled verse and song<o:p></o:p></p><p class=MsoNormal>when judgment drops the final grave<o:p></o:p></p><p class=MsoNormal>over our ravings.<o:p></o:p></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Calibri","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Calibri","sans-serif";color:#632423''>Victor David<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Calibri","sans-serif";color:black''><a href="http://www.victordavid.com">www.victordavid.com</a><o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Verdana","sans-serif";color:black''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><b><span style=''font-size:10.0pt;font-family:"Tahoma","sans-serif"''>From:</span></b><span style=''font-size:10.0pt;font-family:"Tahoma","sans-serif"''> poetrylane2=gmail.com@email.submittable.com [mailto:poetrylane2=gmail.com@email.submittable.com] <b>On Behalf Of </b>Off the Coast<br><b>Sent:</b> Tuesday, July 02, 2013 11:56 AM<br><b>To:</b> Victor David Sandiego<br><b>Subject:</b> [Off the Coast] RE: in the aftermath of this curious war<o:p></o:p></span></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal>July 2, 2013 <br><br>Dear Victor, <br><br>We at Off the Coast are pleased to be publishing your poem “The dark side of devotion” in our summer issue. <br><br>Owing to format changes that may have taken place with our submissions manager as well as standardizing some forms for the page of our journal, we have attached a copy of your poem. This is not an offer for you to rewrite them, but we do want to honor your intention. <br><br>Expect to receive your contributor’s copy by early August. You may purchase additional copies for $5, half the cover price. If you wish to subscribe, you may do so by mail or through our web site www.off-the-coast.com A one-year subscription (four issues) costs $35. <br><br>We hope you will consider sending us your work again. Currently, we have no plans for themed issues. The deadline for our fall issue is September 15th. To submit, send three previously unpublished poems of any style, length or form using our submission manager http://offthecoast.submittable.com/submit or our postal address below. We prefer online submissions. <br><br>Thank you for submitting to Off the Coast. <br><br>Sincerely yours, <br><br>Valerie Lawson and Michael Brown <br>Editors and Publishers <br><br>Off the Coast <br>PO Box 14 <br>Robbinston, ME 04671 <br><br>poetrylane2@gmail.com <br>www.off-the-coast.com <br><br><span style=''border:solid windowtext 1.0pt;padding:0in''><img border=0 width=100 height=100 id="Picture_x0020_1" src="cid:~WRD000.jpg" alt="Description: Image removed by sender."></span><o:p></o:p></p></div></body></html>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (107, E'00000000FC0A50B868F35745AE9D2EED70629592C4142000', 283, E'0', E'The Cresset', E'Marci.Johnson@valpo.edu', E'Victor David Sandiego', E'99poets@victordavid.com', E'[The Cresset] Reply', E'[The Cresset] Reply', '2013-07-01 09:49:04', '2013-07-01 09:49:04', 2, E'Dear Victor David Sandiego,\r\n<br />\r\n<br />Thank you for sending us "Miracles have no names, nor a creator". Each year we receive many wonderful submissions, and the sad fact of the matter is we can only print a handful of them. We are sorry we cannot accept your work for publication at this time. We wish you the best of luck placing it elsewhere.\r\n<br />\r\n<br />Sincerely,\r\n<br />Marci Johnson\r\n<br />The Cresset\r\n<br /><img width="1px" height="1px" src="http://email.email.submittable.com/o/aD03NDYzOWIxY2YzNjYyMjM1MzE4NWJmYzVhY2E4YTBmYiZyPTk5cG9ldHMlNDB2aWN0b3JkYXZpZC5jb20mdD1kZWNsaW5lZCZkPWQ3ZDU5">');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (108, E'00000000FC0A50B868F35745AE9D2EED70629592640C2000', 165, E'1', E'Victor David Sandiego', E'poetry2009@victordavidsandiego.com', E'''editors@cerisepress.com''', E'editors@cerisepress.com', E'RE: Cerise Press - Issue 6 Galley - Victor David Sandiego', E'RE: Cerise Press - Issue 6 Galley - Victor David Sandiego', '2010-07-14 11:03:16', '2010-07-14 11:01:00', 1, E'Hello All -\r\n\r\nThe First Book of Muwadi is a book, a series of connected poems, the (first) story of Muwadi. It really is a cross between one long poem and a book; this is how I work. But I think for this, in italics would be nice, that being the style when referring to a book\x3f In the past, I have always used italics but I didn''t know there was a difference. :)\r\n\r\nThank you for your help!\r\n\r\nVictor David\r\n\r\n\r\n\r\n-----Original Message-----\r\nFrom: editors@cerisepress.com [mailto:editors@cerisepress.com] \r\nSent: Wednesday, July 14, 2010 12:08 AM\r\nTo: Victor David Sandiego\r\nSubject: RE: Cerise Press - Issue 6 Galley - Victor David Sandiego\r\n\r\n\r\nDear Victor David,\r\n\r\nThank you for the note - we can add the line. Is The First Book of\r\nMuwadi a book, or a lengthy poem\x3f\r\n(That is, should we put it in italics, or inside quotes\x3f)\r\n\r\nWarm regards,\r\nKaren, Sally, Fiona\r\n_________________\r\n\r\nCerise Press\r\nhttp://www.cerisepress.com\r\n\r\n> -------- Original Message --------\r\n> Subject: [SPAM] RE: Cerise Press - Issue 6 Galley - Victor David\r\n> Sandiego\r\n> From: "Victor David Sandiego" <poetry2009@victordavidsandiego.com>\r\n> Date: Tue, July 13, 2010 9:35 pm\r\n> To: <editors@cerisepress.com>\r\n> \r\n> \r\n> Hello All -\r\n> \r\n> Thank you. The galley looks good. Almost all of my poems are part of a\r\n> larger work. In this case, "La historia de los ángeles" is part of a larger\r\n> "story" named "The First Book of Muwadi". Could we put this in, perhaps at\r\n> the end in smaller type\x3f\r\n> \r\n> From: The First Book of Muwadi\r\n> \r\n> Thank you.\r\n> \r\n> Victor David\r\n> \r\n> \r\n> ________________________________\r\n> \r\n> From: editors@cerisepress.com [mailto:editors@cerisepress.com]\r\n> Sent: Thursday, July 08, 2010 12:54 AM\r\n> To: poetry2009@victordavidsandiego.com\r\n> Subject: Cerise Press - Issue 6 Galley - Victor David Sandiego\r\n> \r\n> \r\n> Dear Victor,\r\n> \r\n> Enclosed is the galley of  "La historia de los angeles" for the Spring 2011,\r\n> Vol. 2 Issue 6 of Cerise Press. Please let us know if there edits.\r\n> \r\n> Thank you for your time and consideration. We look forward to hearing from\r\n> you.\r\n> \r\n> \r\n> Warm regards,\r\n> Karen, Sally, Fiona\r\n> Editors');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (109, E'00000000FC0A50B868F35745AE9D2EED70629592840C2000', 165, E'1', E'Victor David Sandiego', E'poetry2009@victordavidsandiego.com', E'''editors@cerisepress.com''', E'editors@cerisepress.com', E'RE: Cerise Press - Issue 6 Galley - Victor David Sandiego', E'RE: Cerise Press - Issue 6 Galley - Victor David Sandiego', '2010-07-13 23:35:08', '2010-07-13 23:31:00', 1, E'Hello All -\r\n\r\nThank you. The galley looks good. Almost all of my poems are part of a larger work. In this case, "La historia de los ángeles" is part of a larger "story" named "The First Book of Muwadi". Could we put this in, perhaps at the end in smaller type\x3f\r\n\r\nFrom: The First Book of Muwadi\r\n\r\nThank you.\r\n\r\nVictor David\r\n\r\n\r\n________________________________\r\n\r\nFrom: editors@cerisepress.com [mailto:editors@cerisepress.com]\r\nSent: Thursday, July 08, 2010 12:54 AM\r\nTo: poetry2009@victordavidsandiego.com\r\nSubject: Cerise Press - Issue 6 Galley - Victor David Sandiego\r\n\r\n\r\nDear Victor,\r\n\r\nEnclosed is the galley of  "La historia de los angeles" for the Spring 2011, Vol. 2 Issue 6 of Cerise Press. Please let us know if there edits.\r\n\r\nThank you for your time and consideration. We look forward to hearing from you.\r\n\r\n\r\nWarm regards,\r\nKaren, Sally, Fiona\r\nEditors');
