INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (113, E'00000000FC0A50B868F35745AE9D2EED70629592C4012000', 201, E'0', E'Lewis University', E'lewislitjournal@gmail.com', E'Victor David Sandiego', E'99poets@victordavid.com', E'Re: Sandiego/Poetry/6 poems', E'Re: Sandiego/Poetry/6 poems', '2011-11-08 16:56:18', '2011-11-08 16:56:20', 2, E'<div class="gmail_quote"><blockquote style="margin: 0px 0px 0px 0.8ex; padding-left: 1ex; border-left-color: rgb(204, 204, 204); border-left-width: 1px; border-left-style: solid;" class="gmail_quote"><div class="HOEnZb"><div class="h5">\r\n<div class="gmail_quote"><font size="3" face="Times New Roman">\r\n\r\n</font><p style="margin: 0in 0in 10pt;" class="MsoNormal"><span style="font-family: &quot;Cambria&quot;,&quot;serif&quot;; mso-ascii-theme-font: major-latin; mso-hansi-theme-font: major-latin;"><font size="3">Dear Mr. Sandiego,</font></span></p>\r\n<font size="3" face="Times New Roman">\r\n\r\n</font><p style="margin: 0in 0in 10pt;" class="MsoNormal"><span style="font-family: &quot;Cambria&quot;,&quot;serif&quot;; mso-ascii-theme-font: major-latin; mso-hansi-theme-font: major-latin;"><font size="3">Thank you for submitting your\r\nwork to The Jet Fuel Review, Lewis University’s online literary journal.<span style="mso-spacerun: yes;">  </span>We apologize for delays in getting back to\r\nyou.<span style="mso-spacerun: yes;">  </span>We received many more submissions\r\nfor this issue than we had for the first, which slowed us down in responding.</font></span></p><font size="3" face="Times New Roman">\r\n\r\n</font><p style="margin: 0in 0in 10pt;" class="MsoNormal"><span style="font-family: &quot;Cambria&quot;,&quot;serif&quot;; mso-ascii-theme-font: major-latin; mso-hansi-theme-font: major-latin;"><font size="3">At this time, we have decided not\r\nto publish the works you submitted.<span style="mso-spacerun: yes;">  </span>We\r\nwish you luck in your future endeavors, and would love for you to submit\r\nmaterial again for future issues.</font></span></p><font size="3" face="Times New Roman">\r\n\r\n</font><p style="margin: 0in 0in 10pt;" class="MsoNormal"><span style="font-family: &quot;Cambria&quot;,&quot;serif&quot;; mso-ascii-theme-font: major-latin; mso-hansi-theme-font: major-latin;"><font size="3">Thank you very much,</font></span></p>\r\n<font size="3" face="Times New Roman">\r\n\r\n</font><p style="margin: 0in 0in 10pt;" class="MsoNormal"><span style="font-family: &quot;Cambria&quot;,&quot;serif&quot;; mso-ascii-theme-font: major-latin; mso-hansi-theme-font: major-latin;"><font size="3">The Jet Fuel Review staff</font></span></p>\r\n<font size="3" face="Times New Roman">\r\n\r\n</font><p style="margin: 0in 0in 10pt;" class="MsoNormal"><span style="font-family: &quot;Cambria&quot;,&quot;serif&quot;; mso-ascii-theme-font: major-latin; mso-hansi-theme-font: major-latin;"><font size="3"> </font></span></p>\r\n<font size="3" face="Times New Roman">\r\n\r\n</font></div></div></div></blockquote></div>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (114, E'00000000FC0A50B868F35745AE9D2EED70629592E4062000', 224, E'0', E'Mid-American Review', E'do-not-reply@bgsu.edu', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Your submission to Mid-American Review: "5 poems"', E'Your submission to Mid-American Review: "5 poems"', '2012-07-06 22:10:25', '2012-07-06 22:10:28', 1, E'Dear Victor David Sandiego:\r\n\r\nThank you for the privilege of considering your writing for our journal. We''ve read it carefully, but we regret to report we have decided not to accept it for publication. We wish you luck in placing it another outlet and wish you well with all your writing endeavors.\r\n\r\nThanks for letting me take a look, and please forgive my long delay in responding to you, Victor.\r\n\r\nBest,\r\nKaren Craigo\r\nEditor\r\n.\r\n.\r\n.\r\nAnnouncing the 2012 Fineline Competition for Prose Poems, Short Shorts, and Anything In-Between! First Prize: $1000. Final Judge: Amelia Gray. For more information, visit the link below:\r\n.\r\nhttp://www.bgsu.edu/studentlife/organizations/midamericanreview/fineline.html\r\n\r\n2012-07-06 23:10:25 (GMT -4:00)');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (115, E'00000000FC0A50B868F35745AE9D2EED7062959224052000', 216, E'0', E'TheLindenwoodReview', E'TheLindenwoodReview@lindenwood.edu', E'Victor David Sandiego', E'99poets@victordavid.com', E'RE: Poetry: Submission of 5 poems', E'RE: Poetry: Submission of 5 poems', '2012-03-14 20:48:32', '2012-03-14 20:57:52', 1, E'Victor,\r\n\r\nThank you so much for sharing your work with us. While we appreciate the strengths in your poems, unfortunately they are not quite the right fit for Issue 2 of The Lindenwood Review. We hope that you will consider submitting to Issue 3 of our journal between July 15 and December 15, 2012. We have added readers for our next reading period to ensure a quicker response time for those who submit work to Issue 3. Thank you again for giving us the chance to read your work, and we wish you continued success with your writing.\r\n \r\nSincerely,\r\nThe Editors\r\nThe Lindenwood Review\r\nwww.lindenwood.edu/lindenwoodReview \r\n\r\n________________________________________\r\nFrom: Victor David Sandiego [99poets@victordavid.com]\r\nSent: Monday, November 28, 2011 10:07 AM\r\nTo: TheLindenwoodReview\r\nSubject: Poetry: Submission of 5 poems\r\n\r\nDear Lindenwood Review,\r\n\r\nI''ve attached a Word document that contains 5 poems for your consideration.\r\nThree are from a longer work titled The First Book of Muwadi, and two are\r\nfrom a longer work titled The Second Book of Muwadi.\r\n\r\nAs requested, a brief bio:\r\n\r\n-------\r\nVictor David Sandiego – poeta, baterista, composer – was the winner of the\r\n1st WordStorm Poetry Competition held on Vancouver Island in British\r\nColumbia, a winner of the 2008 Jeanne Lohmann Poetry Prize, and the winner\r\nof the 2009 Crab Creek Review poetry contest. His work appears in various\r\njournals and on public radio. He lives in the high desert of central México.\r\nFor more, see: www.VictorDavid.com.\r\n-------\r\n\r\nThank you. Please let me know if you need more information.\r\n\r\nVictor David Sandiego\r\n\r\n\r\nLindenwood University Email Disclaimer:  http://www.lindenwood.edu/emailPolicy.htm');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (116, E'00000000FC0A50B868F35745AE9D2EED70629592E4092000', 246, E'1', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'''Gulf Coast Business Mgr.''', E'gulfcoast.biz@gmail.com', E'RE: Gulf Coast Barthelme Prize Entry', E'RE: Gulf Coast Barthelme Prize Entry', '2012-09-13 12:09:42', '2012-09-13 12:09:00', 2, E'<html xmlns:v="urn:schemas-microsoft-com:vml" xmlns:o="urn:schemas-microsoft-com:office:office" xmlns:w="urn:schemas-microsoft-com:office:word" xmlns:m="http://schemas.microsoft.com/office/2004/12/omml" xmlns="http://www.w3.org/TR/REC-html40"><head><meta name=Generator content="Microsoft Word 14 (filtered medium)"><style><!--\r\n/* Font Definitions */\r\n@font-face\r\n\t{font-family:Calibri;\r\n\tpanose-1:2 15 5 2 2 2 4 3 2 4;}\r\n@font-face\r\n\t{font-family:Tahoma;\r\n\tpanose-1:2 11 6 4 3 5 4 4 2 4;}\r\n@font-face\r\n\t{font-family:Verdana;\r\n\tpanose-1:2 11 6 4 3 5 4 4 2 4;}\r\n@font-face\r\n\t{font-family:Georgia;\r\n\tpanose-1:2 4 5 2 5 4 5 2 3 3;}\r\n/* Style Definitions */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal\r\n\t{margin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:12.0pt;\r\n\tfont-family:"Times New Roman","serif";}\r\na:link, span.MsoHyperlink\r\n\t{mso-style-priority:99;\r\n\tcolor:blue;\r\n\ttext-decoration:underline;}\r\na:visited, span.MsoHyperlinkFollowed\r\n\t{mso-style-priority:99;\r\n\tcolor:purple;\r\n\ttext-decoration:underline;}\r\nspan.EmailStyle17\r\n\t{mso-style-type:personal-reply;\r\n\tfont-family:"Verdana","sans-serif";\r\n\tcolor:#002060;}\r\n.MsoChpDefault\r\n\t{mso-style-type:export-only;\r\n\tfont-family:"Calibri","sans-serif";}\r\n@page WordSection1\r\n\t{size:8.5in 11.0in;\r\n\tmargin:1.0in 1.0in 1.0in 1.0in;}\r\ndiv.WordSection1\r\n\t{page:WordSection1;}\r\n--></style><!--[if gte mso 9]><xml>\r\n<o:shapedefaults v:ext="edit" spidmax="1026" />\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n<o:shapelayout v:ext="edit">\r\n<o:idmap v:ext="edit" data="1" />\r\n</o:shapelayout></xml><![endif]--></head><body lang=EN-US link=blue vlink=purple><div class=WordSection1><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Verdana","sans-serif";color:#002060''>Hello Sasha &#8211;<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Verdana","sans-serif";color:#002060''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Verdana","sans-serif";color:#002060''>I had left a message in the comment field of my submission explaining that I picked DB prize because it was the only choice in the drop-down category list at the time. I thought you could switch the data internally. Today, I logged back in, withdrew the DB prize entry, and resubmitted. That should get us back on track. Thanks for the note.<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Verdana","sans-serif";color:#002060''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Verdana","sans-serif";color:#002060''>Victor David<o:p></o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Verdana","sans-serif";color:#002060''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><span style=''font-size:11.0pt;font-family:"Verdana","sans-serif";color:#002060''><o:p>&nbsp;</o:p></span></p><p class=MsoNormal><b><span style=''font-size:10.0pt;font-family:"Tahoma","sans-serif"''>From:</span></b><span style=''font-size:10.0pt;font-family:"Tahoma","sans-serif"''> Gulf Coast Business Mgr. [mailto:gulfcoast.biz@gmail.com] <br><b>Sent:</b> Thursday, September 13, 2012 8:32 AM<br><b>To:</b> undisclosed-recipients:<br><b>Subject:</b> Gulf Coast Barthelme Prize Entry<o:p></o:p></span></p><p class=MsoNormal><o:p>&nbsp;</o:p></p><p class=MsoNormal><span style=''font-family:"Georgia","serif"''>Dear Writer:<br><br>We recently noticed that you submitted a piece to <i>Gulf Coast</i>''s 2012 Donald Barthelme<b> </b>Prize for Short Prose;</span><b><span style=''font-family:"Georgia","serif"''> </span></b><span style=''font-size:10.0pt;font-family:"Georgia","serif"''>however, we are not able to find a reading fee payment from you in our system. <span style=''color:red''>&nbsp;</span></span><b><span style=''font-size:10.0pt;font-family:"Georgia","serif";color:red''>If you made this payment with a Paypal account that does not list the name or address that you used when submitting to <i>Gulf Coast</i>, then we will need you to forward us this information so that we can match your submission to the corresponding payment.</span></b><span style=''font-size:10.0pt;font-family:"Georgia","serif";color:red''> </span><span style=''font-size:10.0pt;font-family:"Georgia","serif"''><o:p></o:p></span></p><div><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Georgia","serif"''><o:p>&nbsp;</o:p></span></p></div><div><p class=MsoNormal><span style=''font-size:10.0pt;font-family:"Georgia","serif"''>If you have not yet paid your reading fee, then please follow the link below in order to complete your payment. If we do not receive this payment by<span style=''color:#009900''>&nbsp;</span><b><span style=''color:red''> September 20th</span></b>, then we will have to withdraw your piece from the contest.<o:p></o:p></span></p></div><p class=MsoNormal><span style=''font-family:"Georgia","serif"''><br></span><a href="http://www.gulfcoastmag.org/index.php\x3fn=7&amp;sn=14&amp;paypal&amp;b" target="_blank"><span style=''font-family:"Georgia","serif"''>Click here to pay the Barthelme Contest Reading Fee</span></a><span style=''font-family:"Georgia","serif"''> </span><b><i><span style=''font-size:10.0pt;font-family:"Georgia","serif"''><br>(Remember, you are not required to have a Paypal account in order to make a payment. You may simply use a credit or check card if you would like.</span></i></b><b><span style=''font-size:10.0pt;font-family:"Georgia","serif"''>)</span></b><span style=''font-size:10.0pt;font-family:"Georgia","serif"''><br></span><span style=''font-family:"Georgia","serif"''><br>You may also mail in your submission and payment by following the </span><a href="http://www.gulfcoastmag.org/index.php\x3fn=7" target="_blank"><span style=''font-family:"Georgia","serif"''>instructions listed on <i>Gulf Coast</i>''s website</span></a><span style=''font-family:"Georgia","serif"''>.<br><br>If you do not wish to pay this fee or if you meant for your submission to be considered as a non-contest submission (<b><i>currently, </i>Gulf Coast<i> does not accept general submissions from March 15 through August 31</i></b>), then please log in to the </span><a href="http://www.gulfcoastmag.org/submissions/" target="_blank"><span style=''font-family:"Georgia","serif"''>Gulf Coast Submission Manager</span></a><span style=''font-family:"Georgia","serif"''> and withdraw your piece from the contest. You may resubmit your piece to Gulf Coast at any time.<br><br>Please feel free to email us back if you have any questions or concerns.<br clear=all><br>Thank you,<br>Sasha Khalifeh</span><br clear=all><br>-- <br>Business Manager, GULF COAST<br>A Journal for Literature &amp; Fine Art<br>English Department, University of Houston<br>Houston, TX 77204-3013<br><a href="tel:%28713%29%20743-3223" target="_blank">(713) 743-3223</a><br><a href="http://www.gulfcoastmag.org" target="_blank">http://www.gulfcoastmag.org</a><o:p></o:p></p></div></body></html>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (117, E'00000000FC0A50B868F35745AE9D2EED70629592240C2000', 241, E'0', E'Diverse Voices Quarterly', E'submissions@diversevoicesquarterly.com', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Reply: Diverse Voices Quarterly', E'Reply: Diverse Voices Quarterly', '2012-09-30 13:33:38', '2012-09-30 13:33:39', 1, E'Dear Victor David Sandiego:\r\n\r\nUnfortunately, your work doesn’t fit our needs at this time. Keep writing and celebrating diversity! \r\n\r\nThank you for trying us.\r\n\r\nSincerely,\r\n\r\nThe Editors of Diverse Voices Quarterly\r\n\r\n2012-09-30 14:33:38 (GMT -4:00)');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (118, E'00000000FC0A50B868F35745AE9D2EED70629592240D2000', 238, E'0', E'Four Way Books', E'june2012@fourwaybooks.com', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Reply: Four Way Books June Reading Period', E'Reply: Four Way Books June Reading Period', '2012-10-11 15:39:45', '2012-10-11 15:39:46', 1, E'Dear Victor David Sandiego,\r\n\r\nII want to thank you for submitting your work to Four Way Books in June. I am sorry that we can''t offer you publication for your manuscript, 57 Women of the Earth, at this time.The selection process is never easy for us, and It is truly difficult to pass on work of merit. At times like this, we feel that we are in an impossible position in that we want to be able to publish so much more than we can. \r\n\r\nWe continue to read toward future seasons, and our manuscript readers rotate on a regular\r\nbasis so that returning manuscripts can receive fresh attention. Our readers are seasoned editors, or authors. We do not ask students to read for us. If you find that you want to try your hand with us again, please know that our door is open. We accept submissions through our contest (January 1-March 31 each year). In 2013, James Longenbach will judge for our contest, the Levis Prize, open to all poets writing in English, regardless of past publication history. We will read poetry and short fiction in June for our Open Reading Period. Please check our website at www.fourwaybooks.com at the end of December for our contest guidelines, and at the end of May for our June guidelines.\r\n\r\nWe realize that this is disappointing news. Please know that we are working hard to bring more literature into the world, and we do hope that your work will be placed in the near future. \r\n\r\nWith warmest regards,\r\n\r\nMartha Rhodes, Director\r\nRyan Murphy, Associate Director\r\n\r\n2012-10-11 16:39:42 (GMT -4:00)');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (119, E'00000000FC0A50B868F35745AE9D2EED70629592040C2000', 246, E'0', E'Gulf Coast', E'donotreply@gulfcoastmag.org', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Reply: Gulf Coast', E'Reply: Gulf Coast', '2012-09-28 14:33:30', '2012-09-28 14:33:36', 1, E'Dear Writer:\r\n\r\nThank you for your recent submission to Gulf Coast. We appreciate your patience while we read your work. However, we''re sorry to inform you that your work was not the right fit for us at this time.\r\n\r\nThank you again for submitting, and good luck with all your writing endeavors.\r\n\r\nSincerely,\r\nThe Gulf Coast Editors\r\n\r\n2012-09-28 14:33:04 (GMT -5:00)');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (120, E'00000000FC0A50B868F35745AE9D2EED70629592A40D2000', 254, E'0', E'Iron Horse Literary Review ', E'ihlr.drop@gmail.com', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'Reply: Iron Horse Literary Review', E'Reply: Iron Horse Literary Review', '2012-11-02 08:08:16', '2012-11-02 08:18:18', 2, E'Dear Victor David Sandiego:\r\n<br />\r\n<br />Thank you for submitting your work, "Muwadi in captivity" and other poems, to Iron Horse Literary Review. \r\n<br />\r\n<br />While we appreciate the time and talent that goes into every submission we receive, we can only accept a very small percentage of them.  Unfortunately, this particular submission does not meet our current needs.  \r\n<br />\r\n<br />About your manuscript, our editors said: We would prefer to see more clarity regarding the literal situations of these pieces. Because we weren''t sure what was going on, we had trouble responding to the poems'' tone and emotion.\r\n<br /> \r\n<br />Visit our website at www.ironhorsereview.com to catch up on our latest news, learn about our next submission period, or purchase a sample copy for only $5. You can also follow us on Twitter @IHLR and on Facebook at http://bit.ly/aTwiSW. \r\n<br />\r\n<br />Thank you again for considering our journal, and we wish you success placing this submission elsewhere.\r\n<br />\r\n<br />Sincerely,\r\n<br />\r\n<br />\r\n<br />Landon Houle\r\n<br />Managing Editor\r\n<br />Iron Horse Literary Review\r\n<br />\r\n\r\n<img src="http://email.submishmash.com/wf/open\x3fupn=Ed-2Fova0yVzBcPgrA-2FK6FvUlaUnPKd4VMC1I30rInseENa8TkWXjBD2AEEdVa2xkJhbo7BtPEa9ZO4km52gY9n1dOB7GshVZUzXQmt-2BXTvm25YQbcr8XuC6QPB4Fp5c3o23bausHoMeW6fGHjzgOFvVRv0YN1wl4iR2klYeAewJ0xz-2F7FXx73Ab2v-2FbzP-2FvdMGo6W3sj6-2F8hi-2BopmV-2BH8QA-3D-3D" alt="" width="1" height="1" border="0" style="height:1px !important;width:1px !important;border-width:0 !important;margin-top:0 !important;margin-bottom:0 !important;margin-right:0 !important;margin-left:0 !important;padding-top:0 !important;padding-bottom:0 !important;padding-right:0 !important;padding-left:0 !important;"/>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (121, E'00000000FC0A50B868F35745AE9D2EED70629592C40D2000', 247, E'0', E'The Emerson Review', E'submissions.er@gmail.com', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'Reply: The Emerson Review', E'Reply: The Emerson Review', '2012-11-02 11:06:08', '2012-11-02 11:06:12', 2, E'Dear Victor,\r\n<br />\r\n<br />We appreciated the chance to read "Advice from my Father", "Gratitude" and "Rebirth." We thought these poems displayed vivid imagination, and moments of fresh language, but many of the metaphors were a bit too abstract, making them difficult to connect to. Although the poems were not a good fit for us, we wish you luck with them elsewhere!\r\n<br />\r\n<br />Thank you for submitting, \r\n<br />\r\n<br />Amanda Bondi\r\n<br />The Emerson Review\r\n<br />\r\n\r\n<img src="http://email.submishmash.com/wf/open\x3fupn=Ed-2Fova0yVzBcPgrA-2FK6FvUlaUnPKd4VMC1I30rInseFSCFLXEfLLqd1mFHagBxg2EBhypx7mzbwW6JuRAjUG02DkmuftZA96GgoflSH6-2FIXtd10uNg-2FgCdv7bBfunNORyh6cFbnLc9h6LpkuXuGASK5XGxEHvuyPpN6diu94OXUqy9d9dRselEqMtGzN-2B-2BRcb8PUzqnPiOuCfWp0xrXprA-3D-3D" alt="" width="1" height="1" border="0" style="height:1px !important;width:1px !important;border-width:0 !important;margin-top:0 !important;margin-bottom:0 !important;margin-right:0 !important;margin-left:0 !important;padding-top:0 !important;padding-bottom:0 !important;padding-right:0 !important;padding-left:0 !important;"/>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (122, E'00000000FC0A50B868F35745AE9D2EED70629592840D2000', 242, E'0', E'The Ilanot Review', E'ilanotjournal@gmail.com', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'Reply: The Ilanot Review', E'Reply: The Ilanot Review', '2012-10-29 09:25:57', '2012-10-29 09:26:23', 2, E'Dear Victor David Sandiego,\r\n<br />\r\n<br />Thank you for sending your poems to The Ilanot Review for our consideration. We loved the fascinating imagery and pace of all your work. In particular we liked “the red sky inside me” and spent a lot of time discussing this poem. While we eventually decided that it doesn’t completely fit our theme and the upcoming issue, we want to encourage you to submit other work for future issues. We wish you the best of luck in finding a home for these poems elsewhere.  Good luck.\r\n<br />\r\n<br />Thanks again. \r\n<br />\r\n<br />Sincerely,\r\n<br />Sarah Wetzel and the Editorial Staff\r\n<br />The Ilanot Review\r\n<br /><img src="http://email.submishmash.com/wf/open\x3fupn=Ed-2Fova0yVzBcPgrA-2FK6FvUlaUnPKd4VMC1I30rInseFiBT-2FotQquvBNhPcRDQZ0ue8R3Q4NF0-2Foc4SPQ8lE9djXP-2B-2Bjkd-2BmL5spKFUu8DvjEhvdmHmXGrWPCddxt3HcsO2c1rIkArx2zahW-2BJ3mGkwy8YrORCZv9qcHb-2BEVv1c1vaeFJFms0ttHa3E4rwWrs2X1myi6km2p5qewEeesxEQ-3D-3D" alt="" width="1" height="1" border="0" style="height:1px !important;width:1px !important;border-width:0 !important;margin-top:0 !important;margin-bottom:0 !important;margin-right:0 !important;margin-left:0 !important;padding-top:0 !important;padding-bottom:0 !important;padding-right:0 !important;padding-left:0 !important;"/>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (123, E'00000000FC0A50B868F35745AE9D2EED7062959264082000', 234, E'0', E'Sonya Taaffe', E'editor@taaffe.org', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'Re: [sh-poetry] POETRY SUB: The first death of God', E'Re: [sh-poetry] POETRY SUB: The first death of God', '2012-08-02 18:23:10', '2012-08-02 18:23:25', 1, E'Dear Victor,\r\n\r\nThank you for submitting your poetry to Strange Horizons. We very much\r\nappreciate the chance to read your work, but we are regretfully saying no\r\nto this submission, although there are some beautiful images in here,\r\nbeginning with the opening line. It might fare better as straight flash\r\nfiction than poetry. We wish you luck with placing it elsewhere and look\r\nforward to further work from you in future.\r\n\r\nBest,\r\n\r\nSonya Taaffe\r\nPoetry Co-Editor, Strange Horizons\r\n\r\n\r\n> The first death of God\r\n> =================\r\n> He hadn''t thought to die so imperceptibly. He always imagined he would\r\n> cross\r\n> a dark river of mist in a splendid boat, but he rises early as usual, dips\r\n> bread in his tea, puts on his autumn coat, swings his front door open\r\n> toward\r\n> the south, and greets the ambling daylight in the street.\r\n>\r\n> At first, he cannot see that he is dead. His town is transformed from one\r\n> of\r\n> modest bricks to a metropolis of giant sculptures and statues. He walks\r\n> down\r\n> a great boulevard, up wide entrance steps, and enters an echoing hall.\r\n>\r\n> "God\x3f" he asks - and a voice that took refuge in his mother''s womb\r\n> answers:\r\n>\r\n>         _"God. Do as you will."_\r\n>\r\n> He ascends a curving marble staircase until he emerges on a terrace high\r\n> above the city. A shivering spot of a girl far below catches his eye with\r\n> her fingers hooked upward and he changes one of her tears into a copper\r\n> coin\r\n> and the breeze that bites her bare arms into a sheepskin blanket.\r\n>\r\n> Satisfied, he sits on the fat stone rail of the terrace for a moment and\r\n> admires his desires. He looks outward to the fields around the city, sees\r\n> seven herds of horses mottled on the ground, and he blows a cloud over\r\n> them\r\n> that rains a cure on their galloping sickness.\r\n>\r\n> "God\x3f" he asks - and a voice that ran to the olive orchards with him when\r\n> he\r\n> was a boy answers:\r\n>\r\n>         _"God. Do as you will."_\r\n>\r\n> He looks farther, twists his eyes around the Earth to a town that wears a\r\n> different shade of skin. A dark green river flows next to it. He stretches\r\n> his arm and rearranges the river''s unmade bed. The water leaps from its\r\n> path, rushes through courtyards and flocks of chickens, topples baker''s\r\n> kilns and sweeps goats into culverts. Men shout curses from rooftops.\r\n> Apples\r\n> and the white bellies of snakes float by.\r\n>\r\n> "God\x3f" he asks - and a damp voice, in the same silent manner of his\r\n> father,\r\n> rolls down his cheek and falls past the terrace rail to join the\r\n> evaporating\r\n> morning on the distant street.\r\n>\r\n>\r\n>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (124, E'00000000FC0A50B868F35745AE9D2EED7062959244082000', 235, E'0', E'Sonya Taaffe', E'editor@taaffe.org', E'Victor David Sandiego', E'99poets@VictorDavid.com', E'Re: [sh-poetry] POETRY SUB: Rope swing south', E'Re: [sh-poetry] POETRY SUB: Rope swing south', '2012-08-02 18:24:10', '2012-08-02 18:24:25', 1, E'Dear Victor,\r\n\r\nThank you for submitting your poetry to Strange Horizons. We very much\r\nappreciate the chance to read your work, but we are regretfully saying no\r\nto this submission, too. The last stanza is particularly striking. We wish\r\nyou luck with placing it elsewhere – if you are looking at other\r\nspeculative markets, you may wish to consider Mythic Delirium — and look\r\nforward to further work from you in future.\r\n\r\nBest,\r\n\r\nSonya Taaffe\r\nPoetry Co-Editor, Strange Horizons\r\n\r\n\r\n> Rope swing south\r\n> ==============\r\n> He cannot help but hear her heartbeat\r\n> in the symphonic strain of mules\r\n> that carry him to the burgundy cross.\r\n>\r\n> The edge of the plateau scratches his cheek\r\n> when he twists the rope in his neck\r\n> for a codal glance of the high desert sky\r\n> and he winds the perimeter of the city where she sleeps\r\n> around the notes of his throat\r\n> when evening blows a vascular breeze of flutes over his blood.\r\n>\r\n> The dying pilgrim in his lung climbs the breath of San Cristóbal\r\n> to soothe the bitter-almond taste of scholars\r\n> scalding his gums:\r\n> His country is a spent wolf\r\n> panting at spirits and sparrows\r\n> and he pledges to swallow his adopted tongue whole.\r\n>\r\n> Mountain is blue when he dismounts\r\n> and spreads her face on the roasted wings of his heart.\r\n> Far behind him, in the back alleys of Chorro, _la verdad_\r\n> puts its face to the sun. A plain red stone\r\n> marks a friendship they forged there\r\n> when she transformed the first story of her people\r\n> into _palabras_.\r\n>\r\n> Now the stories march from his mouth with torches\r\n> and assemble on the church steps.\r\n> He comes to her in the footsteps he had seen\r\n> winding from the sea at La Redonda as he sat in the red dusk sky\r\n>\r\n> and he leaves her\r\n> only if God brands him betrayer\r\n> and casts him tethered with blasphemy from the oak branch.\r\n>\r\n>\r\n>');
