INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (145, E'00000000FC0A50B868F35745AE9D2EED7062959244192000', 289, E'0', E'Wolfgang Görtschacher', E'editor@poetrysalzburg.com', E'Victor D. Sandiego', E'99poets@VictorDavid.com', E'Re: Only When Gods Arrive...', E'Re: Only When Gods Arrive...', '2013-10-31 04:19:42', '2013-10-31 04:19:45', 2, E'<html>\r\n  <head>\r\n    \r\n  </head>\r\n  <body text="#000000" bgcolor="#FFFFFF">\r\n    Dear Victor David Sandiego,<br>\r\n    <br>\r\n    thank you very much for your email. It is very good to hear from\r\n    you. I am sorry that letters are so slow these days. Privatisation\r\n    has slowed down the service instead of accelerating it. Your email\r\n    will of course do as confirmation that the proofs of your poem are\r\n    correct. As a little magazine we are always grateful for every\r\n    support we can get, for every single subscription. If you know\r\n    friends and people interested in poetry who would like to be\r\n    informed about your publication and the magazine, please let us know\r\n    their postal and / or email addresses and we will send them a flier.<br>\r\n    <br>\r\n    Yours sincerely,<br>\r\n    <br>\r\n    Wolfgang G&ouml;rtschacher<br>\r\n    <br>\r\n    <br>\r\n    <div class="moz-cite-prefix">Am 30.10.2013 20:15, schrieb Victor D.\r\n      Sandiego:<br>\r\n    </div>\r\n    <blockquote\r\n      cite="mid:000301ced5a4$63a16f00$2ae44d00$@VictorDavid.com"\r\n      type="cite">\r\n      <meta http-equiv="Content-Type" content="text/html;\r\n        charset=ISO-8859-1">\r\n      <meta name="Generator" content="Microsoft Word 14 (filtered\r\n        medium)">\r\n      <style><!--\r\n/* Font Definitions */\r\n@font-face\r\n\t{font-family:Calibri;\r\n\tpanose-1:2 15 5 2 2 2 4 3 2 4;}\r\n@font-face\r\n\t{font-family:Tahoma;\r\n\tpanose-1:2 11 6 4 3 5 4 4 2 4;}\r\n/* Style Definitions */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal\r\n\t{margin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:11.0pt;\r\n\tfont-family:"Calibri","sans-serif";}\r\na:link, span.MsoHyperlink\r\n\t{mso-style-priority:99;\r\n\tcolor:blue;\r\n\ttext-decoration:underline;}\r\na:visited, span.MsoHyperlinkFollowed\r\n\t{mso-style-priority:99;\r\n\tcolor:purple;\r\n\ttext-decoration:underline;}\r\np.MsoPlainText, li.MsoPlainText, div.MsoPlainText\r\n\t{mso-style-priority:99;\r\n\tmso-style-link:"Plain Text Char";\r\n\tmargin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:11.0pt;\r\n\tfont-family:"Calibri","sans-serif";}\r\nspan.PlainTextChar\r\n\t{mso-style-name:"Plain Text Char";\r\n\tmso-style-priority:99;\r\n\tmso-style-link:"Plain Text";\r\n\tfont-family:"Calibri","sans-serif";}\r\nspan.EmailStyle19\r\n\t{mso-style-type:personal;\r\n\tfont-family:"Tahoma","sans-serif";\r\n\tcolor:black;}\r\nspan.EmailStyle20\r\n\t{mso-style-type:personal-compose;}\r\n.MsoChpDefault\r\n\t{mso-style-type:export-only;\r\n\tfont-size:10.0pt;}\r\n@page WordSection1\r\n\t{size:8.5in 11.0in;\r\n\tmargin:1.0in 1.0in 1.0in 1.0in;}\r\ndiv.WordSection1\r\n\t{page:WordSection1;}\r\n--></style><!--[if gte mso 9]><xml>\r\n<o:shapedefaults v:ext="edit" spidmax="1026" />\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n<o:shapelayout v:ext="edit">\r\n<o:idmap v:ext="edit" data="1" />\r\n</o:shapelayout></xml><![endif]-->\r\n      <div class="WordSection1">\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"\r\n            lang="ES-MX">Victor David Sandiego<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"\r\n            lang="ES-MX">A.P. 170 Centro<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"\r\n            lang="ES-MX">36001 Guanajuato, Gto.<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Mexico<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Poetry\r\n            Salzburg Review<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Dr.\r\n            Wolfgang G&ouml;rtschacher<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">University\r\n            of Salzburg<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Dear Dr.\r\n            G&ouml;rtschacher &#8211;<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Yesterday,\r\n            I received your letter informing me that you have accepted\r\n            &nbsp;"Only When Gods Arrive Covered In Red Dust Will We\r\n            Recognize Their Child Faces". <o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">I am very\r\n            pleased to have found such a nice home for this piece, and\r\n            the proof that you sent looks great. &nbsp;Will this email serve\r\n            as confirmation that I''ve seen the proof and that it''s good\r\n            to go\x3f I can also mail PSR by postal mail if needed, but the\r\n            mails appear to be slow since your letter is dated Oct 1 and\r\n            I only just received it.<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Also, I&#8217;d\r\n            be happy to support PSR with a subscription, and will follow\r\n            up using the PayPal / Email methods described on your web\r\n            site.<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Thanks\r\n            again. Please let me know if you need postal mail or whether\r\n            this email works as confirmation.<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Sincerely,<o:p></o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt"><o:p>&nbsp;</o:p></span></p>\r\n        <p class="MsoPlainText"><span style="font-size:12.0pt">Victor\r\n            David Sandiego<o:p></o:p></span></p>\r\n      </div>\r\n    </blockquote>\r\n    <br>\r\n  </body>\r\n</html>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (146, E'00000000FC0A50B868F35745AE9D2EED7062959204192000', 289, E'1', E'Victor D. Sandiego', E'99poets@VictorDavid.com', E'''psr@poetrysalzburg.com''', E'psr@poetrysalzburg.com', E're: Only When Gods Arrive...', E're: Only When Gods Arrive...', '2013-10-30 13:15:24', '2013-10-30 13:15:00', 2, E'<html xmlns:v="urn:schemas-microsoft-com:vml" xmlns:o="urn:schemas-microsoft-com:office:office" xmlns:w="urn:schemas-microsoft-com:office:word" xmlns:m="http://schemas.microsoft.com/office/2004/12/omml" xmlns="http://www.w3.org/TR/REC-html40"><head><meta name=Generator content="Microsoft Word 14 (filtered medium)"><style><!--\r\n/* Font Definitions */\r\n@font-face\r\n\t{font-family:Calibri;\r\n\tpanose-1:2 15 5 2 2 2 4 3 2 4;}\r\n@font-face\r\n\t{font-family:Tahoma;\r\n\tpanose-1:2 11 6 4 3 5 4 4 2 4;}\r\n/* Style Definitions */\r\np.MsoNormal, li.MsoNormal, div.MsoNormal\r\n\t{margin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:11.0pt;\r\n\tfont-family:"Calibri","sans-serif";}\r\na:link, span.MsoHyperlink\r\n\t{mso-style-priority:99;\r\n\tcolor:blue;\r\n\ttext-decoration:underline;}\r\na:visited, span.MsoHyperlinkFollowed\r\n\t{mso-style-priority:99;\r\n\tcolor:purple;\r\n\ttext-decoration:underline;}\r\np.MsoPlainText, li.MsoPlainText, div.MsoPlainText\r\n\t{mso-style-priority:99;\r\n\tmso-style-link:"Plain Text Char";\r\n\tmargin:0in;\r\n\tmargin-bottom:.0001pt;\r\n\tfont-size:11.0pt;\r\n\tfont-family:"Calibri","sans-serif";}\r\nspan.PlainTextChar\r\n\t{mso-style-name:"Plain Text Char";\r\n\tmso-style-priority:99;\r\n\tmso-style-link:"Plain Text";\r\n\tfont-family:"Calibri","sans-serif";}\r\nspan.EmailStyle19\r\n\t{mso-style-type:personal;\r\n\tfont-family:"Tahoma","sans-serif";\r\n\tcolor:black;}\r\nspan.EmailStyle20\r\n\t{mso-style-type:personal-compose;}\r\n.MsoChpDefault\r\n\t{mso-style-type:export-only;\r\n\tfont-size:10.0pt;}\r\n@page WordSection1\r\n\t{size:8.5in 11.0in;\r\n\tmargin:1.0in 1.0in 1.0in 1.0in;}\r\ndiv.WordSection1\r\n\t{page:WordSection1;}\r\n--></style><!--[if gte mso 9]><xml>\r\n<o:shapedefaults v:ext="edit" spidmax="1026" />\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n<o:shapelayout v:ext="edit">\r\n<o:idmap v:ext="edit" data="1" />\r\n</o:shapelayout></xml><![endif]--></head><body lang=EN-US link=blue vlink=purple><div class=WordSection1><p class=MsoPlainText><span lang=ES-MX style=''font-size:12.0pt''>Victor David Sandiego<o:p></o:p></span></p><p class=MsoPlainText><span lang=ES-MX style=''font-size:12.0pt''>A.P. 170 Centro<o:p></o:p></span></p><p class=MsoPlainText><span lang=ES-MX style=''font-size:12.0pt''>36001 Guanajuato, Gto.<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Mexico<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Poetry Salzburg Review<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Dr. Wolfgang Görtschacher<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>University of Salzburg<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Dear Dr. Görtschacher &#8211;<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Yesterday, I received your letter informing me that you have accepted &nbsp;&quot;Only When Gods Arrive Covered In Red Dust Will We Recognize Their Child Faces&quot;. <o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>I am very pleased to have found such a nice home for this piece, and the proof that you sent looks great. &nbsp;Will this email serve as confirmation that I''ve seen the proof and that it''s good to go\x3f I can also mail PSR by postal mail if needed, but the mails appear to be slow since your letter is dated Oct 1 and I only just received it.<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Also, I&#8217;d be happy to support PSR with a subscription, and will follow up using the PayPal / Email methods described on your web site.<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Thanks again. Please let me know if you need postal mail or whether this email works as confirmation.<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Sincerely,<o:p></o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''><o:p>&nbsp;</o:p></span></p><p class=MsoPlainText><span style=''font-size:12.0pt''>Victor David Sandiego<o:p></o:p></span></p></div></body></html>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (147, E'000000009A53EC4398D3BF48BB7911BF7EA7F5EE842F2900', 300, E'0', E'Crazyhorse', E'crazyhorse@cofc.edu', E'Victor David Sandiego', E'99poets@victordavid.com', E'Crazyhorse: Reply', E'Crazyhorse: Reply', '2013-12-01 13:10:25', '2013-12-01 13:10:25', 2, E'Dear Victor David Sandiego,\r\n<br />\r\n<br />Thank you for sending us "The Beautiful Lines of Your Own Private Face". We appreciate the chance to read it. Unfortunately, it doesn''t fit our current needs. \r\n<br />\r\n<br />While our editors decided to pass on your work, we hope that you will consider entering the Crazyhorse Prizes. From January 1st to January 31st, Crazyhorse will be accepting entires of fiction, poetry, and nonfiction. Winners will receive $2,000 and publication. This year''s judges are: Amy Hempel (fiction), C.D. Wright (poetry), and Phillip Lopate (nonfiction). All entries will be considered for publication, and the $20 entry fee includes a one-year subscription to Crazyhorse.\r\n<br />\r\n<br />Thanks again for sending your work. We wish you the best of luck with your writing. \r\n<br />\r\n<br />Best of luck,\r\n<br />\r\n<br />The Eds.\r\n<br />Crazyhorse\r\n<br /><img width="1px" height="1px" alt="" src="http://email.email.submittable.com/o/aT0yMDEzMTIwMTE5MTAwNi4xOTA3MC43Mjg1NSU0MGVtYWlsLnN1Ym1pdHRhYmxlLmNvbSZoPTNkMzZlZGRkNmYyMWNhMGJlODMzNjU2MzlkODQxNWYxJnI9OTlwb2V0cyU0MHZpY3RvcmRhdmlkLmNvbSZ0PWRlY2xpbmVkJmQ9ZDdkNTk">');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (148, E'000000009A53EC4398D3BF48BB7911BF7EA7F5EE64362900', 301, E'0', E'Tell It Slant', E'no-reply@tellitslant.com', E'99poets@victordavid.com', E'99poets@victordavid.com', E'The Worcester Review received your submission from Tell It Slant', E'The Worcester Review received your submission from Tell It Slant', '2013-12-04 09:46:50', '2013-12-04 09:46:52', 2, E'Dear Victor David Sandiego, <br/>\r\n<p>The Worcester Review is now considering your manuscript(s), <i> The Death of Joseph Daniel, Where The Sun Stands Still, and The Incredible Colors of Our Existence </i>, which they received on December 04, 2013 10:46. The biography in your profile will also be available to them.\r\nOnce the editors at The Worcester Review reach a decision about your submission, you will receive an email with their response from Tell It Slant and your My Submissions page will be\r\nupdated to reflect their findings. You can, however, check your account at anytime, after logging in: http://www.tellitslant.com/ </p>\r\n\r\n<br/>\r\n<p>Thank you for submitting your work through Tell It Slant.</p>\r\n<br/>\r\nBest,<br/>\r\nThe delivery people at Tell It Slant<br/>\r\n<br/>');
