INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (206, E'000000009A53EC4398D3BF48BB7911BF7EA7F5EEE4972900', 323, E'0', E'Diana Christine', E'meditate@spiritfirst.org', E'Victor D. Sandiego', E'99poets@victordavid.com', E'Re: Submission: 5th Annual Meditation Poetry Contest', E'Re: Submission: 5th Annual Meditation Poetry Contest', '2014-01-31 11:59:38', '2014-01-31 11:59:40', 2, E'<div dir="ltr"><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">Thank you\r\nfor your entry (entries) in our Spirit First Poetry Contest 2014. Your\r\nsubmission is now registered and entered, and it is in the first round of our\r\nreviewing committee process. We are honored by your participation&mdash;thank you! Winners\r\nwill be announced on or before April 30, 2014. Winning entries will be published\r\non our website at </font></span><a href="http://www.spiritfirst.org/"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#0000ff">www.SpiritFirst.org</font></span></a><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000"> and on our blog at </font></span><a href="http://www.spiritfirst.blogspot.com/"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#0000ff">www.SpiritFirst.blogspot.com</font></span></a><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">. Winners will also be notified by email and,\r\nif possible, by telephone. </font></span></p><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">&nbsp;</font></span></p><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">Thank you\r\nfor being part of this effort to bring into the world more writings on the\r\nthemes of meditation, mindfulness, silence, stillness, and solitude. Your work\r\nis important to us, and to the world. </font></span></p><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">&nbsp;</font></span></p><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">I wish you\r\ngood luck with our contest, and I wish you much continued success in all your\r\nwriting.</font></span></p><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">&nbsp;</font></span></p><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">Diana Christine</font></span></p><font color="#000000" face="Times New Roman" size="3">\r\n\r\n</font><p class="MsoNormal" style="margin:0in 0in 0pt"><span style="font-family:&quot;Calibri&quot;,&quot;sans-serif&quot;;font-size:11pt"><font color="#000000">Director</font></span></p><font color="#000000" face="Times New Roman" size="3