INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (219, E'000000009A53EC4398D3BF48BB7911BF7EA7F5EEA4AF2A00', 325, E'0', E'Better: Culture & Lit', E'better.culture.lit@gmail.com', E'Victor David Sandiego', E'99poets@victordavid.com', E'RE: [Better: Culture & Lit] Better Poetry Contest 2014', E'RE: [Better: Culture & Lit] Better Poetry Contest 2014', '2014-03-31 21:09:02', '2014-03-31 21:09:02', 2, E'Dear Victor David,\r\n<br />\r\n<br />Thank you for submitting to BETTER''s first annual poetry prize, judged by Srikanth Reddy. We will be announcing our winners in the next twelve hours, but we wanted to drop you a line beforehand to say we''re sorry that you have not been selected as the winner.\r\n<br />\r\n<br />We received more than 400 entries this year—many more than we expected—and we were frankly shocked by the overall quality of those entries. The competition was fierce, and we expect that many of those who didn''t win will nevertheless place poems in BETTER some day. \r\n<br />\r\n<br />Keep an eye out for our winners'' announcement, and until then, we hope you''ll keep reading and submitting to BETTER.\r\n<br />\r\n<br />Best wishes,\r\n<br />Sean Bishop, Kent Shaw, & Carolina Ebeid\r\n<br />bettermagazine.org<img width="1px" height="1px" alt="" src="http://email.email.submittable.com/o/aT0yMDE0MDQwMTAzMDkwMS4yMjM5Mi4yMDk2OSU0MGVtYWlsLnN1Ym1pdHRhYmxlLmNvbSZoPTlkNjU1OTdjNjY2ZGEwYWRhNTU4MjBiOTU0OTA3ZWRmJnI9OTlwb2V0cyU0MHZpY3RvcmRhdmlkLmNvbSZkPWQ3ZDU5">');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (220, E'000000009A53EC4398D3BF48BB7911BF7EA7F5EEA4BA2A00', 326, E'0', E'Chagrin River Review', E'editors@chagrinriverreview.com', E'Victor David Sandiego', E'99poets@victordavid.com', E'RE: [Chagrin River Review] In The Revolution Of Streets', E'RE: [Chagrin River Review] In The Revolution Of Streets', '2014-04-02 18:58:07', '2014-04-02 18:58:08', 2, E'Dear Victor David Sandiego,\r\n<br />\r\n<br />Thank you for sending us "In The Revolution Of Streets." We appreciate the chance to read your work.  We regret that it doesn''t quite fit our editorial needs, but we wish you well in your writing and publishing.\r\n<br />\r\n<br />Thanks again, and best of luck.\r\n<br />\r\n<br />Sincerely,\r\n<br />Angie Weaver\r\n<br />Poetry Editor\r\n<br />Chagrin River Review<img width="1px" height="1px" alt="" src="http://email.email.submittable.com/o/aT0yMDE0MDQwMzAwNTgwNi4xNTI1Ny41NTIwJTQwZW1haWwuc3VibWl0dGFibGUuY29tJmg9MGI3ZjcyN2Y4YTBlNTVjMmI5YWVkOTRiN2Q3MGI4YmUmcj05OXBvZXRzJTQwdmljdG9yZGF2aWQuY29tJmQ9ZDdkNTk">');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (221, E'000000009A53EC4398D3BF48BB7911BF7EA7F5EE24FB2A00', 295, E'0', E'Tell It Slant', E'noreply@tellitslant.com', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Your submission of Submissions to Harvard Review to Harvard Review', E'Your submission of Submissions to Harvard Review to Harvard Review', '2014-04-13 23:44:55', '2014-04-13 23:44:56', 2, E'<p>Thank you for submitting your work to&nbsp;<i>Harvard Review</i>. Unfortunately, we are unable to accept it for publication. We hope you will bear in mind that we are only able to publish a fraction of what is submitted to the journal and that we are therefore obliged to reject a large number of publishable pieces. We encourage you to submit your work to other journals and thank you for your interest in&nbsp;<i>Harvard Review</i>.</p>\r\n<p>Regards,&nbsp;</p>\r\n<p>The Editors</p>');
INSERT INTO "submission_message" ("id", "entry_id", "submissionbatchid", "outgoing", "sender_name", "sender_email", "recipient_name", "recipient_email", "subject", "display", "sent_date", "received_date", "body_format", "body") VALUES (222, E'000000009A53EC4398D3BF48BB7911BF7EA7F5EE643B2B00', 301, E'0', E'Tell It Slant', E'noreply@tellitslant.com', E'99poets@victordavid.com', E'99poets@victordavid.com', E'Your submission to The Worcester Review', E'Your submission to The Worcester Review', '2014-04-22 09:10:30', '2014-04-22 09:10:31', 2, E'<p>Thank you for considering the Worcester County Poetry Association&rsquo;s literary journal <i>The Worcester Review </i>as an outlet for your work. We regret that we are unable to accept it for publication. We appreciate your patience; we recognize that we are slow in responding, partly because we are an annual publication and partly because we receive thousands of submissions each   year (which is why we encourage simultaneous submission), but be assured that your work has been read by both an established creative writer and a literary scholar.  We accept less than 3% of our submissions.</p>\r\n<p>While we encourage submitters to familiarize themselves with our journal before submitting, it is our sincere hope that, even if you don''t subscribe to our journal, you will consider supporting other literary journals of your choice. Journals like ours provide an important forum for new literature, a lively and necessary art form.</p><br/><br/>\r\nSubmission Date: 12/04/13 <br/>\r\nManuscript Titles: The Death of Joseph Daniel, Where The Sun Stands Still, and The Incredible Colors of Our Existence <br/>');
