INSERT INTO "user_note" ("note_id", "title", "date_created", "note_text") VALUES (5, E'Inexorable / Inextricable', '2012-03-02 13:23:20', E'{\\rtf1\\ansi\\ansicpg1252\\deff0\\deflang1033{\\fonttbl{\\f0\\fnil\\fcharset0 Microsoft Sans Serif;}}\r\n{\\colortbl ;\\red0\\green0\\blue139;}\r\n\\viewkind4\\uc1\\pard\\cf1\\b\\f0\\fs24 in\\''b7ex\\''b7o\\''b7ra\\''b7ble\\cf0\\b0   (n-ksr--bl)\\par\r\nadj.\\par\r\nNot capable of being persuaded by entreaty; relentless: an inexorable opponent; a feeling of inexorable doom. \\par\r\n\\par\r\n\\cf1\\b in\\''b7ex\\''b7tri\\''b7ca\\''b7ble\\cf0\\b0   (n-kstr-k-bl, nk-strk-bl)\\par\r\nadj.\\par\r\n1.\\par\r\n  a. So intricate or entangled as to make escape impossible: an inextricable maze; an inextricable web of deceit.\\par\r\n  b. Difficult or impossible to disentangle or untie: an inextricable tangle of threads.\\par\r\n  c. Too involved or complicated to solve: an inextricable problem.\\par\r\n\\par\r\n2. Unavoidable; inescapable: bound together by an inextricable fate.\\fs17\\par\r\n}\r\n');
